import axios from 'axios';
import React, { useContext, useState } from 'react';
import { Col, Row, Spinner, Button } from 'react-bootstrap';
import DataTables from '../Components/AcknowledgeDataTable';
import VaccineList from '../Components/VaccinesSelectDropDown'
import { useInput } from '../Data/useInput';
import { userIntelContext } from '../App';
let acknowledgementList = []
let acknowledgementData = null
let wastageList = []
let wastage = null
const Acknowledge = () => {
    const user = useContext(userIntelContext)
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: expirationDate, bind: bindExpirationDate, reset: resetExpirationDate } = useInput('');
    const { value: comment, bind: bindComment, reset: resetComment } = useInput('');
    const { value: pcv, bind: bindPcv, reset: resetPcv } = useInput('');
    const [disabled, setDisabled] = useState(false)
    const [selectedVaccine, setSelectedVaccine] = useState('')
    const resetVaccineSelector = document.getElementById("vaccineSelectionDropdown")
    const [damaged, setDamaged] = useState(null)
    const [spin, setSpin] = useState('')
    const [message, setMessage] = useState('')
    const constraintCheck = (array, id, updatedQuantity) => {
        let x = [];//    
        let stringId = id.toString()
        for (let i in array) {
            if (array[i].vaccineid === stringId) {
                array[i].VaccineQuantity = updatedQuantity;
                x.push(1)
            }
        }
        if (x.includes(1)) {
            return array
        }
        else {
            return 0
        }
    }

    const handleWatsageSubmit = (e) => {
        e.preventDefault();
    }
    const handleWastageList = (e) => {
        e.preventDefault()
        if (constraintCheck(wastageList, selectedVaccine.vaccineId, quantity)) {
            wastageList = constraintCheck(wastageList, selectedVaccine.vaccineId, quantity)
        }
        else {
            wastageList.push({
                "vaccineid": selectedVaccine.vaccineId,
                "vaccineName": selectedVaccine.vaccineName,
                "expirationDate": expirationDate,
                "VaccineQuantity": quantity,
                "comment": comment
            })
        }
        setSelectedVaccine('');
        resetVaccineSelector.value = ""
        resetQuantity()
        resetExpirationDate()
        resetComment()
    }
    const handleAcknowledgmentList = (e) => {
        e.preventDefault()
        if (constraintCheck(acknowledgementList, selectedVaccine.vaccineId, quantity)) {
            acknowledgementList = constraintCheck(acknowledgementList, selectedVaccine.vaccineId, quantity)
        }
        else {
            acknowledgementList.push({
                "vaccineid": selectedVaccine.vaccineId,
                "vaccineName": selectedVaccine.vaccineName,
                "expirationDate": expirationDate,
                "pcvStatus": pcv,
                "VaccineQuantity": quantity,
                "comment": comment
            })
        }
        setSelectedVaccine('');
        resetVaccineSelector.value = ""
        resetQuantity()
        resetExpirationDate()
        resetPcv();
        resetComment()
    }
    const handleChoice = (e) => {
        if (e.target.value === "yes") {
            resetQuantity()
            setSelectedVaccine('')
            setDamaged(true)
        }
        else if (e.target.value === "no") {
            resetQuantity()
            setSelectedVaccine('')
            setDamaged(false)
        }
    }
    const handleAcknowledgmentApiRequest = () => {
        let today = new Date();
        let dd = String(today.getDate()).padStart(2, '0');
        let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        let yyyy = today.getFullYear();

        // today = mm + '/' + dd + '/' + yyyy;
        today = mm + '/' + dd + '/' + yyyy;
        const config = {
            url: 'http://localhost:8765/api/acknowledge',
            headers: {
                'auth': localStorage.getItem('vmisJwt')
            }
        };
        acknowledgementData = {
            from_orgunitId: user.userToken.orgunit,
            from_orgunitName: user.userToken.orgunitname,
            to_orgunitid:user.userToken.parentorgunit,
            to_orgunitname:user.userToken.parentorgunitname,
            date:today,
            acknowledgedItems: acknowledgementList
        }
        setDisabled(true)
        setSpin('border')
        axios.post(config.url, acknowledgementData, { headers: config.headers }).then(res => {
            setDisabled(false)
            setSpin('')
            acknowledgementList.splice(0, acknowledgementList.length)
            acknowledgementData = {}
            setMessage({ type: 'text-center alert alert-success', message: 'Acknowledgement successful' })
            setTimeout(() => {
                setMessage('')
            }, 5000);
        }).catch(() => {
            setDisabled(false)
            setSpin('')
            setMessage({ type: 'text-center alert alert-danger', message: 'Acknowledgement failed' })
            setTimeout(() => {
                setMessage('')
            }, 5000);
        })
    }

    return (
        <div>
            <h4 className='card-header text-center'>Acknowledge</h4>
            <div className='card m-2'>
                <div className='fom-group card-header text-center'>
                    <p>Any damage:</p>
                    <div className="form-check form-check-inline">
                        <input required className="form-check-input form-control" type="radio" name="damage" id="damage1" value="yes" onChange={handleChoice} />
                        <label className="form-check-label" htmlFor="damage1">Yes</label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input required className="form-check-input form-control" type="radio" name="damage" id="damage2" value="no" onChange={handleChoice} />
                        <label className="form-check-label" htmlFor="damage2">No</label>
                    </div>
                </div>
                <p className={message.type}>{message.message}</p>
                <div className="card-body">{damaged === true ?
                    <Row>
                        <Col>
                            <form autocomplete="off" onSubmit={handleWastageList}>
                                <div className='row my-3'>
                                    <div className="col-md-12 ">
                                        <VaccineList onChange={value => setSelectedVaccine(value)} />
                                        <div className='form-group'>
                                            <label htmlFor='quantity'>Quantity:</label>
                                            <input required type='number' name='quantity' className='form-control' {...bindQuantity} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='quantity'>Expiration Date:</label>
                                            <input required type='date' name='expirationdate' className='form-control' {...bindExpirationDate} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='expirationDate'>Comments:</label>
                                            <textarea rows="3" className='form-control' {...bindComment} />
                                        </div>

                                        <div className='form-group'>
                                            <button className='btn btn-success form-control'>Add To list</button>
                                        </div>
                                    </div>
                                </div>
                            </form>
                        </Col>
                        <Col>
                            {wastageList.length > 0 ? <DataTables data={wastageList} type={0} /> : ""}
                        </Col>
                    </Row>
                    : damaged === false ?
                        <Row>
                            <Col>
                                <form autocomplete="off" onSubmit={handleAcknowledgmentList}>
                                    <div className='row my-3'>
                                        <div className="col-md-12">
                                            <VaccineList onChange={value => setSelectedVaccine(value)} disabled={disabled} />
                                            <div className='form-group'>
                                                <label htmlFor='quantity'>Quantity:</label>
                                                <input required type='number' name='quantity' className='form-control' disabled={disabled} {...bindQuantity} />
                                            </div>
                                            <div className='form-group'>
                                                <label htmlFor='quantity'>Expiration Date:</label>
                                                <input required type='date' name='expirationdate' className='form-control' disabled={disabled} {...bindExpirationDate} />
                                            </div>
                                            <div className='form-group'>
                                                <label htmlFor='pcvStatus'>VVM (PCV) Status:</label>
                                                <select name="pcvStatus" className='form-control' required {...bindPcv} disabled={disabled}>
                                                    <option value="">Select one</option>
                                                    <option value="1">1</option>
                                                    <option value="2">2</option>
                                                    <option value="3">3</option>
                                                    <option value="4">4</option>
                                                </select>
                                            </div>
                                            <div className='form-group'>
                                                <label htmlFor='expirationDate'>Comments:</label>
                                                <textarea rows="3" className='form-control' {...bindComment} disabled={disabled} />
                                            </div>

                                            <div className='form-group'>
                                                <button className='btn btn-success form-control' disabled={disabled}>Add to list</button>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </Col>
                            <Col>
                                {acknowledgementList.length > 0 ? <div><DataTables data={acknowledgementList} type={1} />
                                    <Button variant="primary" onClick={handleAcknowledgmentApiRequest} disabled={disabled}>
                                        <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>
                                </div>
                                    : ""}
                            </Col>
                        </Row>
                        : ""}
                </div>
            </div>
        </div>
    )
}

export default Acknowledge
