import {useState,useEffect} from 'react';
import axios from 'axios'
export const UseAxiosGet=(url)=>{
    const [urlData,setUrlData]=useState({
        loading:false,
        data:null,
        error:false
    });

    useEffect(()=>{
        setUrlData({
            loading:true,
            data:null,
            error:false


        });
        axios.get(url,{
            auth:{username:'admin',
            password:'district',
        }}, {
            params: {
              paging: false
            },

        }).then(response=>{
        setUrlData({
            loading:false,
            data:response.data,
            error:false

        })
    }).catch(error=>{
        setUrlData({
            loading:false,
            data:null,
            error:true

        })
    })},[url]);
    if(urlData.data!==null){
    return urlData
    }
}
export default UseAxiosGet;
