import Axios from 'axios';
import React, { useState } from 'react';
import VaccineList from '../Components/VaccinesSelectDropDown'
import { useInput } from '../Data/useInput';
const acknowledgementList = []
const acknowledgement = {}
const wastageList = []
const wastage = {}
const Acknowledge = () => {
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: expirationDate, bind: bindExpirationDate, reset: resetExpirationDate } = useInput('');
    const { value: comment, bind: bindComment, reset: resetComment } = useInput('');
    const { value: pcv, bind: bindPcv, reset: resetPcv } = useInput('');
    const [selectedVaccine, setSelectedVaccine] = useState('')
    const resetVaccineSelector = document.getElementById("vaccineSelectionDropdown")
    const [damaged, setDamaged] = useState(null)
    const constraintCheck = (array, id, updatedQuantity) => {
        let x = [];//    
        let stringId=id.toString()
        for (let i in array) {

            if (array[i].vaccineid ===stringId) {
                array[i].Vaccinequanity = updatedQuantity;
            }
            return array
        }
        if (x === 1) {
            console.log("array CHanged")
        }
        else if (x === 0) {
            console.log("Array not changed")
        }
    }
    const handleWatsageSubmit = (e) => {
        e.preventDefault();
    }
    const handleWastageList = (e) => {
        e.preventDefault()
        // constraintCheck(wastageList,selectedVaccine.vaccineId,quantity)
        wastageList.push({
            "vaccineid": selectedVaccine.vaccineId,
            "vaccineName": selectedVaccine.vaccineName,
            "Vaccinequanity": quantity,
            "comment": comment
        })
        setSelectedVaccine('');
        resetVaccineSelector.value = ""
        resetQuantity()
        resetExpirationDate()
        resetComment()
        console.log(wastageList)
    }
    const handleAcknowledgmentList = () => {

    }
    const handleChoice = (e) => {
        if (e.target.value === "yes") {
            resetQuantity()
            setSelectedVaccine('')
            setDamaged(true)
        }
        else if (e.target.value === "no") {
            resetQuantity()
            setSelectedVaccine('')
            setDamaged(false)
        }
    }
    let data = ''
    const config = {
        method: 'post',
        url: 'http://localhost:8765/api/vaccines/',
        data: data,
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    const handleAcknowledgmentApiRequest = () => {
        Axios.post(config).then(res => {

        }).catch(error => {

        })
    }
    return (
        <div>
            <h4 className='card-header text-center'>Acknowledge</h4>
            <div className='container-fluid'>
                <div className='fom-group offset-md-4'>
                    <p>Any damage:</p>
                    <div className="form-check form-check-inline">
                        <input required className="form-check-input form-control" type="radio" name="damage" id="damage1" value="yes" onChange={handleChoice} />
                        <label className="form-check-label" htmlFor="damage1">Yes</label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input required className="form-check-input form-control" type="radio" name="damage" id="damage2" value="no" onChange={handleChoice} />
                        <label className="form-check-label" htmlFor="damage2">No</label>
                    </div>
                </div>
                <div>{damaged === true ?
                    <form onSubmit={handleWastageList}>
                        <div className='row my-3'>
                            <div className="col-md-4 ">
                                <VaccineList onChange={value => setSelectedVaccine(value)} />
                                <div className='form-group'>
                                    <label htmlFor='quantity'>Quantity:</label>
                                    <input required type='number' name='quantity' className='form-control' {...bindQuantity} />
                                </div>
                                <div className='form-group'>
                                    <label htmlFor='quantity'>Expiration Date:</label>
                                    <input required type='date' name='expirationdate' className='form-control' {...bindExpirationDate} />
                                </div>
                                <div className='form-group'>
                                    <label htmlFor='expirationDate'>Comments:</label>
                                    <textarea rows="3" className='form-control' {...bindComment} />
                                </div>

                                <div className='form-group'>
                                    <button className='btn btn-success form-control'>Add To list</button>
                                </div>
                            </div>
                        </div>
                    </form>
                    : damaged === false ? <form onSubmit={handleWatsageSubmit}>
                        <div className='row my-3'>
                            <div className="col-md-4">
                                <VaccineList onChange={value => setSelectedVaccine(value)} />
                                <div className='form-group'>
                                    <label htmlFor='quantity'>Quantity:</label>
                                    <input required type='number' name='quantity' className='form-control' {...bindQuantity} />
                                </div>
                                <div className='form-group'>
                                    <label htmlFor='quantity'>Expiration Date:</label>
                                    <input required type='date' name='expirationdate' className='form-control' {...bindExpirationDate} />
                                </div>
                                <div className='form-group'>
                                    <label htmlFor='pcvStatus'>VVM (PCV) Status:</label>
                                    <select name="pcvStatus" className='form-control' required {...bindPcv}>
                                        <option value="">Select one</option>
                                        <option value="1">1</option>
                                        <option value="2">2</option>
                                        <option value="3">3</option>
                                        <option value="4">4</option>
                                    </select>
                                </div>
                                <div className='form-group'>
                                    <label htmlFor='expirationDate'>Comments:</label>
                                    <textarea rows="3" className='form-control' {...bindComment} />
                                </div>

                                <div className='form-group'>
                                    <button className='btn btn-success form-control'>Add to list</button>
                                </div>
                            </div>
                        </div>
                    </form> : ""}
                </div>
            </div>
        </div>
    )
}

export default Acknowledge
