import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useInput } from '../Data/useInput';
import { constraintCheck } from '../Data/ConstraintCheck';
const DistributionList = (props) => {
    const [message, setMessage] = useState(null)
    const [getDistributions, setGetDistributions] = useState(null)
    // const { value: damageType, bind: bindDamageType, reset: resetDamageType } = useInput('');
    // const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    // const { value: comment, bind: bindComment, reset: resetComment } = useInput('');
    // const { value: pcv, bind: bindPcv, reset: resetPcv } = useInput('');
    const [acknowledgementList, acknowledgememt] = [[], []]
    const [distributionId, setDistributionId] = useState(null)
    const [disabled, setDisabled] = useState(false)
    const [requestSource, setRequestSource] = useState('')

    useEffect(() => {
        axios.get('http://localhost:8765/api/distribution/acknowledge', {
            headers: {
                'auth': localStorage.getItem('vmisJwt')
            }
        }).then(results => {
            setGetDistributions(results.data)
        }).catch(() =>
            setMessage({ type: 'text-center alert alert-success m-2', message: 'No acknowledgememt list' })
        )
    }, [props.message])
    const handleAcknowledgmentList = (e) => {
        e.preventDefault()
        // if (constraintCheck(acknowledgementList, selectedVaccine.vaccineId, quantity)) {
        //     acknowledgementList = constraintCheck(acknowledgementList, selectedVaccine.vaccineId, quantity)
        // }
        // else {
        //     acknowledgementList.push({
        //         "vaccineid": selectedVaccine.vaccineId,
        //         "vaccineName": selectedVaccine.vaccineName,
        //         "batchNumber": batchNumber,
        //         "pcvStatus": pcv,
        //         "vaccineQuantity": quantity,
        //         "comment": comment
        //     })
        // }
        // setSelectedVaccine('');
        // resetVaccineSelector.value = ""
        // resetQuantity()
        // resetBatchNumber()
        // resetPcv();
        // resetComment()
    }
    console.log(distributionId);
    return (<div>
        {getDistributions ?
            Object.keys(getDistributions).map((post, index) => {
                return (
                    <div key={index}>
                        <hr />
                        <div className="card" key={index}>
                            <div className="card-body" key={index}>
                                <h6 className="card-title" key={index}>Distribution From <b key={index}>{getDistributions[post][0].distribution_origin_name}</b></h6>
                                <button className="btn btn-info" onClick={() => { setDistributionId(post); acknowledgementList.splice(0, acknowledgementList.length); setMessage(''); setRequestSource({ orgunitname: getDistributions[post][0].from_source_orgunit_name, orgunitid: getDistributions[post][0].distribution_origin_id }); }} disabled={disabled}>Confirm Distribution</button>
                            </div>
                        </div>
                    </div>
                )
            }
            )
            : <div className="alert alert-info">nothing to acknowledge</div>}
                                    {requestData && distributionId ? <div className="col-md-9">
                            <div className="card-body">
                                <h3 className='card-header'><b>{requestData[distributionId][0].from_source_orgunit_name} </b></h3>
                                <div className="row">
                                    <div className="col">
                                        <table className="table table-striped table-bordered">
                                            <thead>
                                                <tr>
                                                    <th scope="col">Vaccine Name</th>
                                                    <th scope="col">Quantity ordered</th>
                                                    <th scope="col">Average monthly consumption</th>
                                                    <th scope="col">Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {distributionId ? requestData[distributionId].map((e, i) => (
                                                    <tr key={i}><td>{e.vaccinename}</td><td> {e.quantityrequested}</td><td></td><td><button onClick={() => { vaccineHandover({ vacname: e.vaccinename, vacId: e.vaccine_uuid }); setQuantityRequested(e.quantityrequested);setRequestId(e.request_uuid) }} className="btn btn-info" disabled={disabled}>Distribute</button></td></tr>)) : ""}
                                            </tbody>
                                        </table>
                                    </div>
                                    <div className="col my-2">
                                        <form onSubmit={handleAddToList} autoComplete="off">
                                            <div className='form-group'>
                                                <h5><b>Respond Request</b></h5>
                                                <VaccinesList onChange={value => setVaccine(value)} disabled={true} />
                                            </div>
                                            <div className='form-group'>
                                                <label htmlFor='quantity'>Quantity:</label>
                                                <input type='number' min='0' name='quantity' className='form-control' {...bindQuantity} required disabled={disabled} />
                                            </div>
                                            <p className="text-danger">{inputError}</p>

                                            <div className='form-group'>
                                                <button className='btn btn-success form-control' disabled={disabled}>Add on a list  </button>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                                <div className="col">
                                </div>
                                {distributeVaccine.length ? <div><table className="table table-striped table-bordered">
                                    <thead>
                                        <tr>
                                            <th scope="col">Vaccine Name</th>
                                            <th scope="col">Quantity Requested</th>
                                            <th scope="col">Quantity Distributed</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {distributeVaccine.length ? distributeVaccine.map((key, index) => (
                                            <tr key={index}><td>{key.vaccineName}</td><td>{key.itemRequestedQuantity}</td><td>{key.itemQuatity}</td><td><button onClick={() => deleteDistribution(index)} className="btn btn-danger" disabled={disabled}>Delete</button></td></tr>
                                        )) : ""}
                                    </tbody>
                                </table>

                                    <Button variant="primary" onClick={handleApiSubmission} disabled={disabled}>
                                        <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>

                                </div> : ""}
                            </div>
                        </div> : ""}
    </div >);
}

export default DistributionList;
