const meQuery = {
    me: {
        "resource": "me"
    }
}

const thisUser = [];

const Container = styled.div`
padding: 1rem;
>.stock{
    display: flex; 
    align-items: center;
    justify-content: center;
    font-size: 1rem;
    }
`

function Stock(props) {


    return (
        <Container className='stock row'>
            <div className='container-fluid row'>

                <div className='col-lg-9 text-center border card-header'>
                    <h2 className='card-header'><b>Current Stock Status</b></h2>
                    <Chart />
                </div>
                <div className='col-lg-3 text-center'>
                    <h3 className='card-header'><b>PENDING REQUEST </b></h3>
                    <PendingRequest />
                </div>
            </div>
            {/*<Modal open={modalStatus}>...</Modal>*/}
        </Container>
    )
}

export default Stock
