import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Spinner, Button } from 'react-bootstrap'
import { useInput } from '../Data/useInput'
import VaccinesList from '../Components/VaccinesSelectDropDown'

const dispensedItems = []
function Dispensed() {
    var config = {
        method: 'get',
        url: 'http://localhost:8765/api/dispense',
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    const [show, setShow] = useState(false)
    const { value: child, bind: bindChild, reset: resetChild } = useInput('');
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const [selectedVaccine, setSelectedVaccine] = useState('')
    const [inputError, setInputError] = useState('')
    const [deleted, setDeleted] = useState(0)
    const [spin, setSpin] = useState('')
    const [currentStock, setCurrentStock] = useState('')
    useEffect(() => {
        axios(config).then(res => {
            setCurrentStock(res.data)
        }).catch((e) => console.log("no stock "+e))
    }, [Dispensed])
    const handleSubmit = (e) => {
        e.preventDefault()
        // if (quantity > currentStock[vaccine]) {
        //     setInputError("The Quantity specified can not be bigger than stock")
        // }
        // else {
            dispensedItems.push({ "vaccineName": selectedVaccine.VaccineName, "vaccineid": selectedVaccine.vaccineId, "childVaccined": child, "stockQuantityDispensed": quantity });
            // currentStock[vaccine] = currentStock[vaccine] - quantity
            setShow(true)
            resetChild()
            resetQuantity()
            setInputError('')
            setSelectedVaccine('')

        // }
    }
    /**
     * {
	"0": {
		"vaccinestockid": "2",
		"batch_number": "555",
		"expiration_date": "2020-11-09T22:00:00.000Z",
		"quantity": -5432,
		"dose_per_vial": 4,
		"vvm_status": 1,
		"vaccineid": "1",
		"orgunitid": "AJjh2qrFJf3",
		"orgunitname": "Nyabizi HP"
	}
}
     */
    const handleDelete = (e) => {
        dispensedItems.splice(e, 1)
        setInterval(() => {
            setDeleted(deleted+1)
        }, 50);
        setDeleted(false)
    }
    const handleApiSubmission = () => (
        [setSpin('border'),
        document.getElementById("mySubmitBtn").setAttribute("disabled", ""),
        axios.get("https://dev.hisprwanda.org/hiv/organisationUnits/").then(res => {
            document.getElementById("mySubmitBtn").removeAttribute("disabled", "");
            setShow(false);
            setSpin('');
            dispensedItems.splice(0, dispensedItems.length);

        }).catch(error => {
            console.log(error)
        }),
        Dispensed.current = true
        ])
    return (
        <div>
            <h4 className="card-header text-center">Dispensed Vaccines</h4>
            <div className="container-fluid">
                <div className="card-body">
                    <div className="row">
                        <div className="col-md-5">
                            <h3 className="text-title">Current Stock</h3>
                            <table className="table table-striped table-bordered">
                                <thead>
                                    <tr>
                                        <th scope="col">Vaccine Name</th>
                                        <th scope="col">Vaccine Stock Quantity </th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {Object.keys(currentStock).map(key => (
                                        <tr key={key}><td key={key[0]}>{currentStock[key].shortName}</td><td>{currentStock[key].quantity}</td></tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <div className="col">
                            <form onSubmit={handleSubmit} autoComplete="off">
                                <VaccinesList onChange={value => setSelectedVaccine(value)} />
                                <div className='form-group'>
                                    <label>Children vaccinated last month:</label>
                                    <input type='number' name='child' className='form-control p-4' min='0' required placeholder='Number of children vaccinated last month' {...bindChild} />
                                </div>
                                <div className='form-group'>
                                    <label>Quantity of Vaccine to be dispensed:</label>
                                    <input type='number' name='quantity' className='form-control p-4' min='0' required {...bindQuantity} placeholder='Quantity of needed Vaccine' />
                                </div>
                                <p className="text-danger">{inputError}</p>
                                <button className="btn btn-success">Add to list</button>
                            </form>
                        </div>
                    </div>
                    {show && dispensedItems.length > 0 ? <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th scope="col">Vaccine Name</th>
                                    <th scope="col">Children Vaccinated </th>
                                    <th scope="col">Vaccine Stock Quantity </th>
                                </tr>
                            </thead>
                            <tbody>
                                {dispensedItems.map((key, index) => (
                                    <tr key={key}><td key={key}>{key.vaccineName}</td><td>{key.childVaccined}</td><td>{key.stockQuantityDispensed}</td><td><Button variant="danger" onClick={() => handleDelete(index)}>Delete</Button></td></tr>
                                ))}
                            </tbody>
                        </table>
                        <Button variant="primary" id="mySubmitBtn" onClick={handleApiSubmission}>
                            <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>

                    </div> : ""}
                </div>
            </div>
        </div>
    )
}

export default Dispensed
