import React, { useState, useEffect } from 'react'
import { Bar, Pie } from 'react-chartjs-2';
import axios from 'axios';
import { Card } from 'react-bootstrap';
function Chart() {
    let config = {
        method: 'post',
        url: 'http://localhost:8765/api/stock',
        headers: {
            "auth": localStorage.getItem("vmisJwt"),
        },
    };
    const labelName = "Current Stock";
    const [vaccineStock, setVaccineStock] = useState(null)
    useEffect(() => {
        axios(config).then(res => {
            setVaccineStock(res.data)
        }).catch((error) => {
            console.log(error)
        })
    }, [config.url])
    const data = {
        labels: ["Stock status"],
        datasets: []
    };
    for (const i in vaccineStock) {
        if (vaccineStock.hasOwnProperty(i)) {
            const element = vaccineStock[i];
            data.datasets.push({
                label: element.vaccineName,
                data: element.quantity,
                backgroundColor: element.color,
                borderColor: element.borderColor,
                borderWidth: 1
            })
        }
    }
    return (
        <div className='container'>
            <Bar data={data} title='Stock status' />
            <hr />
            <hr />
            <div className="container text-center font-weight-bold">
                <div className='text-center'>
                    <div className='row'>
                        {/* {vaccineStock? Object.keys(vaccineStock).map(key => (
                            <Card>
                                <Card.Header className={`bg-${vaccineStock[key].headerColor} text-center text-light`}>{vaccineStock[key].vaccineName} </Card.Header>
                                <Card.Body>
                                    {vaccineStock[key].quantity}
                            </Card.Body>
                            </Card>
                        )):""} */}

                        <div className='col-md-1'></div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-success'>
                            12<br />
                            BCG
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-success'>
                            33<br />
                            DBCG
                        </div>
                        <div className='col-md-2  border text-secondary rounded-pill alert alert-success'>
                            52<br />
                            VPO
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-danger'>
                            3<br />
                            IPV
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-warning'>
                            7<br />
                            Pentavalent
                        </div>
                    </div>
                    <div className='row'>
                        <div className='col-md-1'></div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-warning'>
                            6<br />
                            Pneumocoque
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-success'>
                            20<br />
                            Rotavirus
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-warning'>
                            9<br />
                            MR
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-danger'>
                            1<br />
                            DMR
                        </div>
                        <div className='col-md-2 border text-secondary rounded-pill alert alert-danger'>
                            4<br />
                            VAT
                        </div>
                    </div>

                    </div>
                </div>
            </div>
        // </div>
    )
}
export default Chart
