import React, { useEffect, useState } from 'react';
import axios from 'axios'
const VaccinesList = (props) => {
    const [vaccines, setVaccines] = useState('')

    var config = {
        method: 'get',
        url: 'http://localhost:8765/api/vaccines/',
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    useEffect(() => {
        axios(config)
            .then(function (response) {
                setVaccines(response.data)
            })
            .catch(function (error) {
                console.log(error);
            });
        return vaccines
    }, [props.dependency,config])
    const getSelectedText = (elementId) => {
        let elt = document.getElementById(elementId);
        if (elt.selectedIndex === -1) return null;
        return elt.options[elt.selectedIndex].text;
    };
    const handleVaccineSelect = (e) => {
        props.onChange({ vaccineId: e.target.value, vaccineName: getSelectedText("vaccineSelectionDropdown")})
    }
    return (<div className='form-group'>
        <label htmlFor='vaccineSelection'>Select Vaccine:</label>
        <select name="vaccineSelection" className='form-control' id="vaccineSelectionDropdown" required disabled={props.disabled} onChange={handleVaccineSelect}>
            <option value="">None</option>
            {vaccines ? vaccines.map((x, index) => <option value={vaccines[index].vaccineid} key={index}>{vaccines[index].shortname}</option>) : ""}
        </select>
    </div>);
}

export default VaccinesList;
