import React, { useState,useContext } from 'react';
import Image from '../MediaFiles/pev.png';
import OrgData from './PopModalOrgUnits';
import { NavLink, useLocation } from 'react-router-dom';
import * as AiIcons from 'react-icons/ai';
import axios from 'axios';
import * as GiIcons from 'react-icons/gi';
import * as BsIcons from 'react-icons/bs';
import {userIntelContext} from '../App'
function Sidebar(props) {
    const userInfo=useContext(userIntelContext)
    console.log()
    const user = {
        username: "digiraneza",
        auth: localStorage.getItem("vmisJwt")
    }
    const userData = JSON.stringify(user)
    const location = useLocation()
    const [selectedOrgUnit, setSelectedOrgUnit] = useState("")
    props.onChange(selectedOrgUnit)
    const handleLogout = () => {
        if (localStorage.getItem("vmisJwt") !== null) {
            axios.put('https://dev.hisprwanda.org/vmis/api/auth', userData).then(res => {
                localStorage.removeItem("vmisJwt")
                window.location.reload()
            }).catch(error => {
                console.log(error)
            })
            localStorage.removeItem("vmisJwt")
            window.location.reload()
        }
    }
    var base64Url = localStorage.getItem('vmisJwt').split('.')[1];
    var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    var jsonPayload = decodeURIComponent(atob(base64).split('').map(function (c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
    var SidebarData = [];
    if (JSON.parse(jsonPayload).userToken.orgunitlevel === '1') {
        SidebarData = [
            {
                title: 'Home',
                path: '/',
                icon: <AiIcons.AiFillHome />,
                cName: 'nav-text'
            },
            {
                title: 'Vaccine',
                path: '/Vaccine',
                icon: <AiIcons.AiOutlineFileAdd />,
                cName: 'nav-text'
            },
            {
                title: 'Users',
                path: '/users',
                icon: <AiIcons.AiOutlineUser />,
                cName: 'nav-text'
            },
            {
                title: 'Distribute',
                path: '/Distribute',
                icon: <AiIcons.AiOutlineStock />,
                cName: 'nav-text'
            },
            {
                title: 'Redistribute',
                path: '/redistribute',
                icon: <BsIcons.BsArrowReturnLeft />,
                cName: 'nav-text'
            },
            {
                title: 'Wastage',
                path: '/wastage',
                icon: <BsIcons.BsTrash />,
                cName: 'nav-text'
            },
        ]
    } else if (JSON.parse(jsonPayload).userToken.orgunitlevel === '4') {
        SidebarData = [
            {
                title: 'Home',
                path: '/',
                icon: <AiIcons.AiFillHome />,
                cName: 'nav-text'
            },
            {
                title: 'Distribute',
                path: '/Distribute',
                icon: <AiIcons.AiOutlineStock />,
                cName: 'nav-text'
            },
            {
                title: 'Request',
                path: '/request',
                icon: <AiIcons.AiOutlinePullRequest />,
                cName: 'nav-text'
            },
            {
                title: 'Dispensed',
                path: '/dispensed',
                icon: <AiIcons.AiOutlineStock />,
                cName: 'nav-text'
            },
            {
                title: 'Return',
                path: '/return',
                icon: <BsIcons.BsArrowReturnLeft />,
                cName: 'nav-text'
            },
            {
                title: 'Redistribute',
                path: '/return',
                icon: <BsIcons.BsArrowReturnLeft />,
                cName: 'nav-text'
            },
            {
                title: 'Acknowledge',
                path: '/acknowledge',
                icon: <GiIcons.GiReceiveMoney />,
                cName: 'nav-text'
            },
            {
                title: 'Wastage',
                path: '/wastage',
                icon: <BsIcons.BsTrash />,
                cName: 'nav-text'
            },
        ]
    } else if (JSON.parse(jsonPayload).userToken.orgunitlevel === '6') {
        SidebarData = [
            {
                title: 'Home',
                path: '/',
                icon: <AiIcons.AiFillHome />,
                cName: 'nav-text'
            },
            {
                title: 'Vaccine',
                path: '/Vaccine',
                icon: <AiIcons.AiOutlineFileAdd />,
                cName: 'nav-text'
            },
            {
                title: 'Request',
                path: '/request',
                icon: <AiIcons.AiOutlinePullRequest />,
                cName: 'nav-text'
            },
            {
                title: 'Dispensed',
                path: '/dispensed',
                icon: <AiIcons.AiOutlineStock />,
                cName: 'nav-text'
            },
            {
                title: 'Redistribute',
                path: '/return',
                icon: <BsIcons.BsArrowReturnLeft />,
                cName: 'nav-text'
            },
            {
                title: 'Return',
                path: '/return',
                icon: <BsIcons.BsArrowReturnLeft />,
                cName: 'nav-text'
            },
            {
                title: 'Acknowledge',
                path: '/acknowledge',
                icon: <GiIcons.GiReceiveMoney />,
                cName: 'nav-text'
            },
            {
                title: 'Wastage',
                path: '/wastage',
                icon: <BsIcons.BsTrash />,
                cName: 'nav-text'
            },
        ]
    }
    return (
        <div className="">
            <div>
                <div className="m-2 p-2 border rounded text-light">
                    <div className="text-center">
                        <img className="text-center profile-img m-2" src={Image} alt="profile" />
                    </div>
                    {/* <span className='card-header text-center'>Profile</span> */}
                    <div className="text-left">
                        <p>Username: {user.username}</p>
                        {console.log(user)}
                        <p><u>Name: {userInfo.surname}</u></p>
                    </div>
                    <div className="text-center">
                        {location.pathname === "/" ?
                            <OrgData onChange={value => setSelectedOrgUnit(value)} btnName="Change Stock" btnColor="btn btn-info" /> : ""
                        }
                    </div>
                </div>
            </div>
            <ul className="SidebarList">
                {
                    SidebarData.map((val, key) => {
                        return (
                            <li key={key} className="row">
                                <NavLink className="row" to={val.path} key={key} exact activeClassName="active">
                                    <div className="row">
                                        <div id="icon">{val.icon}  </div>{" "}
                                        <div id="title">{val.title}  </div>
                                    </div>
                                </NavLink>
                            </li>
                        );
                    })
                }
                <li className="row" onClick={handleLogout}>
                    <div className="row">
                        <div id="icon"><AiIcons.AiOutlineLogout />  </div>{" "}
                        <div id="title">Logout </div>
                    </div>
                </li>
            </ul>
        </div>
    )
}
export default Sidebar
