import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Spinner, Button } from 'react-bootstrap'
import { useInput } from '../Data/useInput';
import { constraintCheck } from '../Data/ConstraintCheck';
import VaccinesList from '../Components/VaccinesSelectDropDown'
const distributeVaccine = []
const DistributionList = (props) => {
    const [message, setMessage] = useState(null)
    const [getDistributions, setGetDistributions] = useState(null)
    const [acknowledgementList, acknowledgememt] = [[], []]
    const [disabled, setDisabled] = useState(false)
    const [requestSource, setRequestSource] = useState('')
    const [distributionId, setDistributionId] = useState('')
    const [vaccine, setVaccine] = useState('');
    const [quantityRequested, setQuantityRequested] = useState('')
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: batchNumber, bind: bindBatchNumber, reset: resetBatchNumber } = useInput('');
    const { value: comment, bind: bindComment, reset: resetComment } = useInput('');
    const [inputError, setInputError] = useState('')
    const [Deleted, setDeleted] = useState(false)
    const [distributed, setDistributed] = useState(0)
    const [spin, setSpin] = useState('')
    const [requestData, setRequestData] = useState('')
    const [requestId, setRequestId] = useState(null)
    useEffect(() => {
        axios.get('http://localhost:8765/api/distribution/acknowledge', {
            headers: {
                'auth': localStorage.getItem('vmisJwt')
            }
        }).then(results => {
            setGetDistributions(results.data)
        }).catch(() =>
            setMessage({ type: 'text-center alert alert-success m-2', message: 'No acknowledgememt list' })
        )
    }, [props.message])
    const vaccineHandover = (vac) => {
        const mySelectElement = document.getElementById("vaccineSelectionDropdown");
        mySelectElement.value = vac.vacId
        mySelectElement.dispatchEvent(
            new MouseEvent('change', {
                bubbles: true,
                cancelable: true,
                view: window,
            }));
    }
    const handleAddToList = (evt) => {
        evt.preventDefault();
        if (vaccine && quantityRequested && quantity) {
            distributeVaccine.push({
                "vaccineName": vaccine.vaccineName,
                "vaccineId": vaccine.vaccineId,
                "itemRequestedQuantity": quantityRequested,
                "itemQuatity": quantity,
            });
            setVaccine('');
            document.getElementById("vaccineSelectionDropdown").value = "";
            resetQuantity();
            setInputError('')
        }
        else {
            setInputError('No vaccine to Distribute')
        }
    }
    const deleteDistribution = (e) => {
        distributeVaccine.splice(e, 1)
        setInterval(() => {
            setDeleted(true)
        }, 50);
        setDeleted(false)
    }
    const handleAcknowledgmentList = (e) => {
        e.preventDefault()
    }
    console.log(distributionId);
    return (<div>
        <div className="row">
        {getDistributions && getDistributions.length>0 ?
            Object.keys(getDistributions).map((post, index) => {
                return (
                    <div key={index} className="col">
                        <div className="card" key={index}>
                            <div className="card-body" key={index}>
                                <h6 className="card-title" key={index}>Distribution From <b key={index}>{getDistributions[post][0].distribution_origin_name}</b></h6>
                                <button className="btn btn-info" onClick={() => { setDistributionId(post); acknowledgementList.splice(0, acknowledgementList.length); setMessage(''); setRequestSource({ orgunitname: getDistributions[post][0].distribution_origin_name, orgunitid: getDistributions[post][0].distribution_origin_id }); }} disabled={disabled}>Confirm Distribution</button>
                            </div>
                        </div>
                    </div>
                )
            }
            )
            : <div className="col-md-9"><div className="alert alert-info">nothing to acknowledge</div></div>}
        {getDistributions && distributionId ? <div className="col-md-7">
            <div className="card-body">
                <h3 className='card-header'><b>{getDistributions[distributionId][0].distribution_origin_name} </b></h3>
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th scope="col">Vaccine Name</th>
                                    <th scope="col">Quantity Requested</th>
                                    <th scope="col">Quantity Supplied</th>
                                    <th scope="col">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {distributionId ? getDistributions[distributionId].map((e, i) => (
                                    <tr key={i}><td>{e.vaccinename}</td><td> {e.quantity_requested}</td><td>{e.quantity_supplied}</td><td><button onClick={() => { vaccineHandover({ vacname: e.vaccinename, vacId: e.vaccine_uuid }); setQuantityRequested(e.quantityrequested); setRequestId(e.request_uuid) }} className="btn btn-info" disabled={disabled}>Acknowledge</button></td></tr>)) : ""}
                            </tbody>
                        </table>
                    <div className="col my-2">
                        <form onSubmit={handleAddToList} autoComplete="off">
                            <div className='form-group'>
                                <h5><b>Respond Request</b></h5>
                                <VaccinesList onChange={value => setVaccine(value)} disabled={true} />

                            </div>
                            <div className='form-group'>
                                <label htmlFor='quantity'>Quantity Received:</label>
                                <input type='number' min='0' name='quantity' className='form-control' {...bindQuantity} required disabled={disabled} />
                            </div>
                            <div className='form-group'>
                                <label htmlFor='quantity'>Batch Number:</label>
                                <input type='text' min='0' name='batchNumber' className='form-control' {...bindBatchNumber} required disabled={disabled} />
                            </div>
                            <div className='form-group'>
                                <label htmlFor='comment'>Comment:</label>
                                <textarea className="form-control" disabled={disabled}></textarea>
                            </div>
                            <p className="text-danger">{inputError}</p>

                            <div className='form-group'>
                                <button className='btn btn-success form-control' disabled={disabled}>Add on a list  </button>
                            </div>
                        </form>
                    </div>
                {distributeVaccine.length ? <div><table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th scope="col">Vaccine Name</th>
                            <th scope="col">Quantity Requested</th>
                            <th scope="col">Quantity Supplied</th>
                        </tr>
                    </thead>
                    <tbody>
                        {distributeVaccine.length ? distributeVaccine.map((key, index) => (
                            <tr key={index}><td>{key.vaccineName}</td><td>{key.itemRequestedQuantity}</td><td>{key.itemQuatity}</td><td><button onClick={() => deleteDistribution(index)} className="btn btn-danger" disabled={disabled}>Delete</button></td></tr>
                        )) : ""}
                    </tbody>
                </table>

                    <Button variant="primary" disabled={disabled}>
                        <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>

                </div> : ""}
            </div>
        </div> : ""}
        </div>
    </div >);
}

export default DistributionList;
