import React, { useState } from 'react';
import styled from 'styled-components';
import PendingRequest from '../Components/PendingRequest';
import PopRequest from '../Components/PopModalRequests';
import { useInput } from '../Data/useInput';
const Container = styled.div`
>.distribute{
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    }
`


function Distribute() {
    const [distributionData, setDistributionData] = useState("")
    const [value, setValue] = useState(false);
    const { value: vaccine, bind: bindVaccine, reset: resetVaccine } = useInput('');
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');

    function changeView() {
        return setValue(true);
    }

    const distributeVaccine={}
    const handleSubmit = (evt) => {
        evt.preventDefault();
        distributeVaccine.push({
            "vaccine": vaccine,
            "quantity": quantity,
        });
        resetVaccine();
        resetQuantity();
    }

    const pendingRequest = [
        {
            "requestTitle": "Vaccine request",
            "requestOrigin": " From Masaka Hospital",
            "request": {
                "BSG": "30",
                "DMR": "490",
                "IPV": "70",
                "VAT": "900",
                "DBCG": "805"
            },
            "responce": {
                "BSG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Item request",
            "requestOrigin": "From Kibagabaga Hospital",
            "request": {
                "BSG": "100",
                "DMR": "40",
                "IPV": "810",
                "VAT": "600",
                "DBCG": "50"
            },
            "responce": {
                "BSG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Item Request",
            "requestOrigin": "From Masaka Hospital",
            "request": {
                "BSG": "50",
                "DMR": "190",
                "IPV": "74",
                "VAT": "50",
                "DBCG": "800"
            },
            "responce": {
                "BSG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Vaccine Request",
            "requestOrigin": "From CHU/K",
            "request": {
                "BSG": "300",
                "DMR": "250",
                "IPV": "70",
                "VAT": "90",
                "DBCG": "500"
            },
            "responce": {
                "BSG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Vaccine Request",
            "requestOrigin": "From Gahini Hospital",
            "request": {
                "BSG": "125",
                "DMR": "10",
                "IPV": "80",
                "VAT": "150",
                "DBCG": "1000"
            },
            "responce": {
                "BSG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
    ]
    return (
        <div className='distribute'>
            <h2 className='card-header text-center'>Distribute Vaccine</h2>
            <br />
            <br />
            <div>
                <div className='row'>
                    <div className='col-md-3 text-center'>
                        <h3 className='card-header'><b>Pending request </b></h3>
                        <div>
                            <div>
                                <div >
                                    {pendingRequest.map(post => {
                                        return (
                                            <div>
                                                {/*
                            <div className='alert alert-secondary' role='alert' aria-live="assertive" aria-atomic='true'>
                                <div className='card-title alert alert-light'>
                                    <h4 className='font-weight-bold'><b><u>{post.requestTitle}</u></b></h4>
                                </div>
                                <hr />
                                <div className='card-text text-center'>
                                    {post.requestOrigin}
                                </div>
                                <hr />
                                <div className='text-righ'>
                                    <button className='btn btn-secondary'>Check request</button>
                                </div>
                            </div>
                            */}
                                                <hr />
                                                <div className="card w-100">
                                                    <div className="card-body">
                                                        <h5 className="card-title"><b>{post.requestOrigin}</b></h5>
                                                        {/* <PopRequest data={post}/> */}
                                                        <button className="btn btn-info" onClick={()=>setDistributionData(post)}>Check this request</button>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>

                      {distributionData? <div className="col-md-9">
                           <div className="card">
                        <h3 className='card-header'><b>{distributionData.requestOrigin} </b></h3>
                        <div className="row">
                        <div className="col">
                           <table className="table table-striped table-bordered">
                                <thead>
                                    <tr>
                                        <th scope="col">Vaccine Name</th>
                                        <th scope="col">Quantity ordered</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {distributionData? Object.entries(distributionData.request).map(e=>(
                                <tr key={e}><td key={e}>{e[0]}</td><td> {e[1]}</td></tr>)):""}

                                </tbody>
                            </table>
                            </div>
                            <div className="col my-2">
                            <div className='form-group'>
                            <label for='vaccineSelection'>Select Vaccine:</label>
                            <select name="vaccineSelection" className='form-control' >
                                <option value="BCG">BCG</option>
                                <option value="DBCG">DBCG</option>
                                <option value="VPO">VPO</option>
                                <option value="IPV">IPV</option>
                                <option value="Pentavalent">Pentavalent</option>
                                <option value="Pneumocoque">Pneumocoque</option>
                                <option value="Rotavirus">Rotavirus</option>
                                <option value="MR">MR</option>
                                <option value="DMR">DMR</option>
                                <option value="VAT">VAT</option>
                            </select>
                        </div>
                        <div className='form-group'>
                            <label for='quantity'>Quantity:</label>
                            <input type='text' name='quantity' className='form-control' />
                        </div>
                        <div className='form-group'></div>
                        <div className='form-group'></div>
                        <div className='form-group'>
                            <button className='btn btn-success form-control'>Add on a list  </button>
                        </div>
                            </div>
                            </div>
                            <div className="col">

                    </div>
                           </div>
                           </div> :""}            
                </div>
            </div>
        </div>
    )
}

export default Distribute
