import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios'
import { useInput } from '../Data/useInput';
import Image from '../MediaFiles/pevSmall.png'
import { userIntelContext } from '../App';
import { Tabs, Tab, Card, Table, Row, Col } from 'react-bootstrap/'
var vaccineRequest = [];
var today = new Date();
var date = today.getDate() + '-' + (today.getMonth() + 1) + '-' + today.getFullYear();

function Request() {
    const userInfo = useContext(userIntelContext)
    const [disabled, setDisabled] = useState(false)
    const [value, setValue] = useState(false);
    const [vaccineList, setVaccineList] = useState('')
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: child, bind: bindChild, reset: resetChild } = useInput('');
    const [selectedVaccine, setSelectedVaccine] = useState('')
    const [requestMade, setRequestMade] = useState(0)
    const [message, setMessage] = useState('')
    const [myRequests, setMyRequests] = useState('')
    function changeView() {
        return setValue(true);
    }
    const getSelectedText = (elementId) => {
        let elt = document.getElementById(elementId);
        if (elt.selectedIndex === -1) return null;
        return elt.options[elt.selectedIndex].text;
    };
    const hanldeSelectBoxInput = (e) => {
        setSelectedVaccine({ vaccineName: getSelectedText(e.currentTarget.id), vaccineId: e.target.value })
    }
    const handleSubmit = (evt) => {
        evt.preventDefault();
        vaccineRequest.push({
            "itemName": selectedVaccine.vaccineName,
            "itemId": selectedVaccine.vaccineId,
            "itemQuantity": quantity,
            "itemNofChild": child,
            "currentStock": (quantity * 100) % 7
        });
        document.getElementById("vaccineSelectionDropdown").value = ""
        resetQuantity();
        resetChild();
    }
    var config = {
        method: 'get',
        url: 'http://localhost:8765/api/vaccines/',
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    useEffect(() => {
        axios(config)
            .then(function (response) {
                setVaccineList(response.data)
                setRequestMade(requestMade + 1)
            })
            .catch(function (error) {
                console.log(error);
            });
        return vaccineList
    }, [config.url])
    const handleApiSubmition = () => {
        setDisabled(false)
        const vaccineRequestedApi = {
            requestFromOrgUnitId: userInfo.userToken.orgunit,
            requestFromOrgUnitName: userInfo.userToken.orgunitname,
            requestToOrgUnitId: userInfo.userToken.parentorgunit,
            requestToOrgUnitName: userInfo.userToken.parentorgunitname,
            requestedItems: vaccineRequest
        }
        if (vaccineRequestedApi) {
            const data = JSON.stringify(vaccineRequestedApi)
            axios.post('http://localhost:8765/api/request', data, {
                headers: {
                    "auth": localStorage.getItem("vmisJwt"),
                    "Content-Type": "Application/Json"
                }
            }).then(() => {
                vaccineRequest.splice(0, vaccineRequest.length);
                setMessage({ message: 'Request sent successfully', code: 'text-center text-success' })
                setDisabled(true)
                setInterval(() => {
                    setMessage('');
                    setValue()

                }, 3000);
            }).catch(() => {
                setMessage({ message: 'Request could not be sent please try again later', code: 'text-center text-danger' })
                setInterval(() => {
                    setMessage('');
                }, 5000);
            })
        }
    }
    useEffect(() => {
        axios.get('http://localhost:8765/api/request', {
            headers: {
                "auth": localStorage.getItem("vmisJwt"),
                "Content-Type": "Application/Json"
            }
        }).then(res => {
            setMyRequests(res.data)
        }).catch(error => {
            console.log(error)
        })
        return () => {
        }
    }, [requestMade])
    return (
        <div>
            <h4 className='card-header text-center'>Request Vaccine</h4>
            <div className="m-3">
                <Tabs defaultActiveKey="request" transition={false} id="noanim-tab-example">
                    <Tab eventKey="request" title="Requests">
                        <div className="my-3">
                            {Object.keys(myRequests).map((key, index) => {
                                return (
                                <Card border="secondary" style={{ width: '18rem' }} className="m-2" key={index}>
                                    <Card.Header>{myRequests[key][0].status}</Card.Header>
                                    <Card.Body>
                                        <Card.Title>{myRequests[key][0].date}</Card.Title>
                                        <Card.Text>
                                            <Table responsive size="sm">
                                                <thead>
                                                    <tr>
                                                        <th>Vaccine</th>
                                                        <th>Quantity</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {myRequests[key].map((key1, index1) => {
                                                        console.log(key1.quantityrequested)
                                                        return (<tr key={index1}><td>{key1.vaccinename}</td><td>{key1.quantityrequested}</td></tr>)
                                                    })}
                                                </tbody>
                                            </Table>
                                        </Card.Text>
                                    </Card.Body>
                                </Card>
                                )
                            })}

                        </div>
                    </Tab>
                    <Tab eventKey="requestmake" title="Make a request">

                        <div className='container-fluid'>
                            {value ?
                                <div className="card-body">
                                    <div className='text-center'>
                                        <div className='row'>
                                            <div className='col-md-4'>
                                                <h2>Republic of Rwanda</h2>
                                                <h2>Ministry of Health</h2>
                                            </div>
                                            <div className='col-md-4'>
                                                <img src={Image} alt='myIg' /><br />
                                                <b>PEV/EPI</b>
                                            </div>
                                            <div className='col-md-4'>
                                                <b><h3>Date: {date}</h3></b>
                                            </div>
                                        </div>
                                        <div className='alert alert-primary'>
                                            <h3><b><u>VACCINE ORDER FORM</u></b></h3>
                                        </div>
                                        <div>
                                            <table className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th scope="col"></th>
                                                        <th scope="col">Children vaccinated last month</th>
                                                        <th scope="col">No. of doses in stock</th>
                                                        <th scope="col">Quantity ordered in doses</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {vaccineRequest.map((post, index) => {
                                                        return (
                                                            <tr key={index}>
                                                                <th scope="row">{post.itemName}</th>
                                                                <td>{post.itemNofChild}</td>
                                                                <td>{post.currentStock}</td>
                                                                <td>{post.itemQuantity}</td>
                                                            </tr>
                                                        )
                                                    })}
                                                </tbody>
                                            </table>
                                            <p className={message.code}>{message.message}</p>
                                            <button className='btn btn-success' onClick={() => handleApiSubmition()} disabled={disabled}>Submit</button>
                                        </div>
                                    </div>
                                </div> :
                                <div>
                                    <div className='card-body row offset-md-1'>
                                        <form className='col-md-5' onSubmit={handleSubmit}>
                                            <div className="">
                                                <div className='form-group'>
                                                    <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                                                    <select name="vaccineSelection" className='form-control' id="vaccineSelectionDropdown" required onChange={hanldeSelectBoxInput}>
                                                        <option value="">None</option>
                                                        {vaccineList ? vaccineList.map((x, index) => <option value={vaccineList[index].vaccineid}>{vaccineList[index].shortname}</option>) : ""}
                                                    </select>
                                                </div>
                                                <div className='form-group'>
                                                    <label>Children vaccinated last month:</label>
                                                    <input type='text' name='child' className='form-control' placeholder='Number of children vaccinated last month' {...bindChild} />
                                                </div>
                                                <div className='form-group'>
                                                    <label>Quantity:</label>
                                                    <input type='text' name='quantity' className='form-control' placeholder='Quantity of needed item' {...bindQuantity} />
                                                </div>
                                                <div className='form-group'></div>
                                                <div className='form-group'></div>
                                                <div className='form-group'>
                                                    <input type="submit" value="Add on a list" className='btn btn-success form-control' />
                                                </div>
                                            </div>
                                        </form>
                                        <div className='col-md-6 offset-md-1 text-center'>
                                            {vaccineRequest.length > 0 ?
                                                <div>
                                                    <br />
                                                    <h3><u><b>List of items on request</b></u></h3>
                                                    <br />
                                                    <br />
                                                    <div>
                                                        <div>
                                                            <div className='card'>
                                                                <table className="table table-striped table-bordered">
                                                                    <thead>
                                                                        <tr>
                                                                            <th scope="col">Vaccine name</th>
                                                                            <th scope="col">Current Stock</th>
                                                                            <th scope="col">Requested</th>
                                                                            <th scope="col">Last month usage</th>
                                                                        </tr>
                                                                    </thead>
                                                                    <tbody>
                                                                        {vaccineRequest.map(post => {
                                                                            return (
                                                                                <tr>
                                                                                    <th scope="row">{post.itemName}</th>
                                                                                    <td>{post.currentStock}</td>
                                                                                    <td>{post.itemQuantity}</td>
                                                                                    <td>{post.itemNofChild}</td>
                                                                                </tr>
                                                                            )
                                                                        })}
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                        </div>
                                                        <br />
                                                        <button className='btn btn-success form-control' onClick={changeView}>Submit request  </button>
                                                    </div>
                                                </div> : ""}
                                        </div>
                                    </div>
                                </div>}
                        </div>
                    </Tab>
                </Tabs>
            </div>
        </div >
    )
}

export default Request
