import {useInput} from '../Data/useInput'
import VaccineList from '../Components/VaccinesSelectDropDown';
const Filter = () => {
    const { value: period, bind: bindPeriod, reset: resetPeriod } = useInput('');
    const { value: date, bind: bindDate, reset: resetDate } = useInput('');
    const [selectedVaccine, setSelectedVaccine] = useState(null)
    return (
        <div>
            <div>
                <div className="form-group">
                    <label htmlFor="specificDate">Pick a date</label>
                    <input type="date" className="form-control m-1" id="specificDate" onChange={()=>props.onChange(e.target.value)}/>
                </div>
                <label htmlFor="period">Choose period</label>
                <div className="form-group ">
                    <input type="Radio" name="period" value="6" id="period" {...bindPeriod} />
                    <label htmlFor="period" className="mx-2">6 months</label>
                    <input type="Radio" name="period" value="12" id="period" {...bindPeriod} />
                    <label htmlFor="period" className="mx-2">12 months</label>
                </div>
                <VaccineList onChange={value=>setSelectedVaccine(value)}/>
            </div>
        </div>
    );
}

export default Filter;
