import React, { useState } from 'react';
import axios from 'axios';
import { GetOrgUnits } from '../Data/OrgDataHook';
import OrgData from './PopModalOrgUnits';
import {Spinner,Button} from 'react-bootstrap'
const RegistrationForm = () => {
    const [selectedOrgUnit, setselectedOrgUnit] = useState("")
    const [error, setError] = useState("")
    const [spin, setSpin] = useState('')
    const formData = { username: "", name: "", surname: "", email: "", phone: "", orgunit: "", orgunitlevel: '', password: "", parentorgunit: "" }
    const getOrg = GetOrgUnits(`https://dev.hisprwanda.org/hiv/api/organisationUnits/${selectedOrgUnit}`);
    function handleSubmit(event) {
        event.preventDefault();
        document.getElementById("mySubmitBtn").setAttribute("disabled", "")
        const form = event.currentTarget;
        if (getOrg.data) {
            formData.username = form[0].value; formData.name = form[1].value; formData.surname = form[2].value; formData.phone = form[3].value;
            formData.email = form[4].value; formData.password = form[5].value; formData.orgunit = form[7].value; formData.orgunitlevel = getOrg.data.level;
            let data = null
            // formData.orgUnitLevel = getOrg.data.level
            data = JSON.stringify(formData)
            if (getOrg) {
                axios.post("http://192.168.0.120:8765/api/users", data, {
                    headers: {
                        "auth": localStorage.getItem("vmisJwt"),
                        "Content-Type": "Application/Json"
                    }
                }).then(results => {
                    document.getElementById("mySubmitBtn").setAttribute("disabled", "")

                }).catch(e => {
                    setError("User could not be created")
                })
            }
        }
    }
    return (
        <div>
            <div className="container">
                <div className=''>

                    <div style={{ padding: "5px" }}>
                        <form onSubmit={handleSubmit} autoComplete="off">
                            <input type="text" className="aformControl m-3 p-2" name="username" required placeholder="Username" />
                            <input type="text" className="aformControl m-3 p-2" name="name" required placeholder="Name" />
                            <br />
                            <input type="text" className="aformControl m-3 p-2" name="surname" required placeholder="Surname" />
                            <input type="number" className="aformControl m-3 p-2" name="phone" required placeholder="Phone Number" />
                            <br />
                            <input type="email" className="aformControl m-3 p-2" name="email" required placeholder="email" />
                            <input type="password" className="aformControl m-3 p-2" name="password" required placeholder="Password" />
                            <br />
                            <div className="m-3">
                                <OrgData onChange={value => setselectedOrgUnit(value)} btnName="Select user Org Unit" btnColor="btn btn-warning" />
                                <input type="hidden" className="formControl" id="myOrgInput" value={selectedOrgUnit} name="orgUnit" />
                            </div>
                            <p className="text-danger">{error}</p>
                            <Button variant="primary" id="mySubmitBtn">
                                <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Create user</Button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default RegistrationForm;
