import React, { useContext, useState } from 'react';
import Chart from '../Components/Chart';
import Filter from './ChartFilter';
import { userIntelContext } from '../App'
function Stock(props) {
    const [filterDate, setFilterDate] = useState(null)
    const userData = useContext(userIntelContext)
    let orgunit = null
    if (!props.stockId.orgUnitId) {
        orgunit = userData.userToken
    }
    return (
        <div>
            <div className="container-fluid">
                <div className='my-3'>
                    <div className='row'>
                        {/* <div className='col-lg-12 text-center border card-header'> */}
                        {props.stockId.orgUnitId ?
                            <div className='col-lg-9 text-center border card-header'>
                                <h4 className='card-header'><b>{props.stockId.orgUnitName} Current Stock Status</b></h4>
                                <Chart requestOrgunitId={props.stockId.orgUnitId} date={filterDate} />
                            </div>
                            : <div className='col-lg-9 text-center border card-header'>
                                {/* <div className='col-lg-12 text-center border card-header'> */}
                                <h4 className='card-header'><b>{orgunit.orgUnitname} Current Stock Status</b></h4>
                                <Chart requestOrgunitId={orgunit.orgunit} date={filterDate} />
                            </div>}
                        <div className='col-3 text-center border card-header'>
                            <h4 className='card-header'><b>{props.stockId.orgUnitName} Filter</b></h4>
                            <Filter onChange={date => setFilterDate(date)} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Stock
