import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Spinner, Button, Form, Row, Col, Card } from 'react-bootstrap'
import { useInput } from '../Data/useInput';
import { constraintCheck } from '../Data/ConstraintCheck';
import VaccinesList from '../Components/VaccinesSelectDropDown'
import { HttpRequests } from '../Data/HttpRequest';
let [acknowledgementList, acknowledgeVaccine, acknowledgementData] = [[], [], null]
const Distributions = (props) => {
    const [message, setMessage] = useState('')
    const [distributionId, setDistributionId] = useState(null)
    const [acknowledgeVaccine, setAcknowledgeVaccine] = useState(null)
    const [quantityRequested, setQuantityRequested] = useState(null)
    const [vaccine, setVaccine] = useState('');
    const [disabled, setDisabled] = useState(false)
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: batchNumber, bind: bindBatchNumber, reset: resetBatchNumber } = useInput('');
    const { value: pcvStatus, bind: bindPcvStatus, reset: resetPcvStatus } = useInput('');
    const { value: comments, bind: bindComments, reset: resetComments } = useInput('');
    const [inputError, setInputError] = useState(null)
    const [spin, setSpin] = useState(initialState)
    const config = {
        method: 'get',
        url: 'http://localhost:8765/api/distribution/acknowledge',
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    let distributionData = HttpRequests(config)
    const vaccineHandover = (vac) => {
        const mySelectElement = document.getElementById("vaccineSelectionDropdown");
        mySelectElement.value = vac.vacId
        mySelectElement.dispatchEvent(
            new MouseEvent('change', {
                bubbles: true,
                cancelable: true,
                view: window,
            }));
    }
    const handleAddToList = (evt) => {
        evt.preventDefault();
        if (vaccine && quantityRequested && quantity) {
            acknowledgeVaccine.push({
                "vaccineName": vaccine.vaccineName,
                "vaccineId": vaccine.vaccineId,
                "itemRequestedQuantity": quantityRequested,
                "itemQuatity": quantity,
            });
            setVaccine('');
            document.getElementById("vaccineSelectionDropdown").value = "";
            resetQuantity();
            setInputError('')
        }
        else {
            setInputError('No vaccine to Distribute')
        }
    }
    console.log(distributionId);
    return (<div>
        <Row>
            {distributionData !== undefined && distributionData.data ? <Col>
                {Object.keys(distributionData.data).map(key => (
                    <Card>
                        <Card.Header>from {distributionData.data[key][0].date}</Card.Header>
                        <button className="btn btn-info" onClick={() => ([setDistributionId(key)])}>Acknowledge</button>
                    </Card>
                ))}
                {distributionData && distributionId ? <div className="col-md-9">
                    <div className="card-body">
                        <h3 className='card-header'><b>{distributionData[distributionId][0].from_source_orgunit_name} </b></h3>
                        <div className="row">
                            <div className="col">
                                <table className="table table-striped table-bordered">
                                    <thead>
                                        <tr>
                                            <th scope="col">Vaccine Name</th>
                                            <th scope="col">Quantity ordered</th>
                                            <th scope="col">Average monthly consumption</th>
                                            <th scope="col">Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {distributionId ? distributionData[distributionId].map((e, i) => (
                                            <tr key={i}><td>{e.vaccinename}</td><td> {e.quantityrequested}</td><td></td><td><button onClick={() => { vaccineHandover({ vacname: e.vaccinename, vacId: e.vaccine_uuid }); setQuantityRequested(e.quantityrequested); setDistributionId(e.request_uuid) }} className="btn btn-info" disabled={disabled}>Distribute</button></td></tr>)) : ""}
                                    </tbody>
                                </table>
                            </div>
                            <div className="col my-2">
                                <form onSubmit={handleAddToList} autoComplete="off">
                                    <div className='form-group'>
                                        <h5><b>Respond Request</b></h5>
                                        <VaccinesList onChange={value => setVaccine(value)} disabled={true} />
                                    </div>
                                    <div className='form-group'>
                                        <label htmlFor='quantity'>Quantity:</label>
                                        <input type='number' min='0' name='quantity' className='form-control' {...bindQuantity} required disabled={disabled} />
                                    </div>
                                    <p className="text-danger">{inputError}</p>

                                    <div className='form-group'>
                                        <button className='btn btn-success form-control' disabled={disabled}>Add on a list  </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="col">
                        </div>
                        {acknowledgeVaccine.length ? <div><table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th scope="col">Vaccine Name</th>
                                    <th scope="col">Quantity Requested</th>
                                    <th scope="col">Quantity Distributed</th>
                                </tr>
                            </thead>
                            <tbody>
                                {acknowledgeVaccine.length ? acknowledgeVaccine.map((key, index) => (
                                    <tr key={index}><td>{key.vaccineName}</td><td>{key.itemRequestedQuantity}</td><td>{key.itemQuatity}</td><td><button onClick={() => deleteDistribution(index)} className="btn btn-danger" disabled={disabled}>Delete</button></td></tr>
                                )) : ""}
                            </tbody>
                        </table>

                            <Button variant="primary" onClick={handleApiSubmission} disabled={disabled}>
                                <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>

                        </div> : ""}
                    </div>
                </div> : ""}
            </Col> : distributionData !== undefined && distributionData.loading ? <Col><Spinner animation={"border"} /></Col> : distributionData !== undefined && distributionData.error ? <Col><div className="alert alert-danger">Something went wrong</div></Col> : <Col><div className="alert alert-info">No un Acknowledged Distribution</div></Col>}
        </Row>
    </div>)
}

export default Distributions;
