import {useState,useEffect} from 'react';
import axios from 'axios'
export const HttpRequests=(config)=>{
    const [urlData,setUrlData]=useState({
        loading:false,
        data:null,
        error:false
    });

    useEffect(()=>{
        setUrlData({
            loading:true,
            data:null,
            error:false
        });
        axios(config).then(response=>{
        setUrlData({
            loading:false,
            data:response.data,
            error:false

        })
    }).catch(error=>{
        setUrlData({
            loading:false,
            data:null,
            error:true

        })
    })},[config.url]);
    if(urlData.data!==null){
    return urlData
    }
}
