import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Spinner, Button, Form } from 'react-bootstrap'
import { useInput } from '../Data/useInput';
import { constraintCheck } from '../Data/ConstraintCheck';
import VaccinesList from '../Components/VaccinesSelectDropDown'
const distributeVaccine = []
const DistributionList = (props) => {
    const [message, setMessage] = useState(null)
    const [getDistributions, setGetDistributions] = useState({
        data: null,
        loading: false,
        error: false
    })
    const [acknowledgementList, acknowledgememt] = [[], []]
    const [disabled, setDisabled] = useState(false)
    const [requestSource, setRequestSource] = useState('')
    const [distributionId, setDistributionId] = useState('')
    const [vaccine, setVaccine] = useState('');
    const [quantityRequested, setQuantityRequested] = useState('')
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const { value: batchNumber, bind: bindBatchNumber, reset: resetBatchNumber } = useInput('');
    const { value: pcvValue, bind: bindPcvValue, reset: resetPcvValue } = useInput('');
    const { value: comment, bind: bindComment, reset: resetComment } = useInput('');
    const [inputError, setInputError] = useState('')
    const [Deleted, setDeleted] = useState(false)
    const [distributed, setDistributed] = useState(0)
    const [spin, setSpin] = useState('')
    const [requestData, setRequestData] = useState('')
    const [requestId, setRequestId] = useState(null)
    useEffect(() => {
        axios.get('http://localhost:8765/api/distribution/acknowledge', {
            headers: {
                'auth': localStorage.getItem('vmisJwt')
            }
        }).then(results => {
            setGetDistributions(results.data)
        }).catch(() =>
            setMessage({ type: 'text-center alert alert-success m-2', message: 'No acknowledgememt list' })
        )
    }, [props.message])
    const vaccineHandover = (vac) => {
        const mySelectElement = document.getElementById("vaccineSelectionDropdown");
        mySelectElement.value = vac.vacId
        mySelectElement.dispatchEvent(
            new MouseEvent('change', {
                bubbles: true,
                cancelable: true,
                view: window,
            }));
    }
    const handleAddToList = (evt) => {
        evt.preventDefault();
        if (vaccine && quantityRequested && quantity) {
            distributeVaccine.push({
                "vaccineName": vaccine.vaccineName,
                "vaccineId": vaccine.vaccineId,
                "itemRequestedQuantity": quantityRequested,
                "itemQuatity": quantity,
