import React,{useState} from 'react';
import axios from 'axios'
import { Row ,Col,Table} from 'react-bootstrap';
import {userData} from './userData'
const DataTables = (props) => {
    const [gotData, setGotData] = useState(false)
    const [q,setQ]=useState("")
    const data=axios.get("http://192.168.0.120:8765/api/users",{
        headers:{
            auth:localStorage.getItem("vmisJwt")
        }
    }).then(res=>{
        setGotData(true)
    }).catch(error=>{
        setGotData(false)
    })
    console.log(data);
    const searchQuery=(rows)=>(rows.filter((row)=>(row.username.toLowerCase().indexOf(q)>-1)));
    return (
        <div>
            <Row>
                <Col>
                <div className="form-group">
                <input type="text" placeholder="Search" className="form-control" onChange={(e)=>setQ(e.target.value)}/>
                </div>
                <Table striped bordered hover size="md" responsive>
  <thead>
    <tr>
      <th>#</th>
      <th>Username</th>
      <th>Email</th>
      <th>Organisation Unit</th>
    </tr>
  </thead>
  <tbody>
      {/* {
          searchQuery(userData).map((user,index)=>(
          <tr><td>{index}</td>
          <td>{user.username}</td><td>{user.email}</td><td>{user.organisationUnit}</td></tr>
          ))
      } */}

  </tbody>
</Table>
                </Col>
            </Row>
        </div>
    );
}

export default DataTables;
