import React, { useState } from 'react';
import { SidebarData } from './SidebarData';
import Image from '../MediaFiles/pev.png';
import styled from 'styled-components';
import OrgData from './PopModalOrgUnits';
import { Link } from 'react-router-dom';


const Container = styled.div`
    margin-top: 1rem;
    margin-left: 0.3rem;
    margin-right: 0rem;
    padding: 0.5rem;
    border: 1px solid white;
    border-radius: 10px;
    position: relative;
    color: white;
`
const ProfileImg = styled.img`
    height: 3rem;
`
const ProfileName = styled.h2`
    font-size: 1rem;
    font-weight: 200;
    color: ${({ theme }) => theme.textColor};
`
function Sidebar(props) {
    const [selectedOrgUnit, setSelectedOrgUnit] = useState("")
    props.onChange(selectedOrgUnit)
    return (
        <div className="">
            <Container>
                <div className="text-center">
                    <ProfileImg className="text-center img-rounded" src={Image} />
                </div>
                <h4 className='card-header text-center'><b>Profile</b></h4>
                <ProfileName>
                    <div className="text-left">
                        <h4 className="text-center"><u><b>USER INFORMATION</b></u></h4>
                        <h5><b><u>Username:</u></b></h5>
                        <h4 className="text-center"><b>Test</b></h4>
                        <h5><b><u>Name:</u></b></h5>
                        <h4 className="text-center"><b>Test</b></h4>
                        <h5><b><u>Organization Unit:</u></b></h5>
                        <h4 className="text-center"><b>Test</b></h4>
                    </div>
                    <div className="text-center">
                        {window.location.pathname === "/" ?
                            <OrgData onChange={value => setSelectedOrgUnit(value)} btnName="Change Stock" btnColor="btn btn-info" /> : ""
                        }
                    </div>
                </ProfileName>
            </Container>
            <ul className="SidebarList">
                {
                    SidebarData.map((val, key) => {
                        return (
                            <li
                                key={key}
                                className=""

                                >

                                {/* <div id="title">{val.title}  </div> */}

                                <Link className="text-light row"to={val.path} id={window.location.pathname === val.path ? "active" : ""}>
                                <div className="row">
                                <div id="icon">{val.icon}  </div>{" "}
                                <div id="title">{val.title}  </div>
                                </div>
                                </Link>

                            </li>
                        );
                    })
                }
            </ul>

        </div>
    )
}

export default Sidebar
