import React, { useState,useEffect } from 'react';
import axios from 'axios'
import { useInput } from '../Data/useInput';
import Image from '../MediaFiles/pevSmall.png'
var vaccineRequest = [];
var today = new Date();
var date = today.getDate() + '-' + (today.getMonth() + 1) + '-' + today.getFullYear();

function Request() {
    const [value, setValue] = useState(false);
    const [vaccineList,setVaccineList]=useState('')
    const { value: name, bind: bindName, reset: resetName } = useInput('');
    const { value: number, bind: bindNumber, reset: resetNumber } = useInput('');
    const { value: child, bind: bindChild, reset: resetChild } = useInput('');
    const [selectedVaccine, setSelectedVaccine] = useState('')
    function changeView() {
        return setValue(true);
    }
    const getSelectedText = (elementId) => {
        let elt = document.getElementById(elementId);
        if (elt.selectedIndex === -1) return null;
        return elt.options[elt.selectedIndex].text;
    };
    const hanldeSelectBoxInput = (e) => {
        setSelectedVaccine({ vaccineName: getSelectedText(e.currentTarget.id), vaccineId: e.target.value })
    }
    const handleSubmit = (evt) => {
        evt.preventDefault();
        vaccineRequest.push({
            "itemName": selectedVaccine.vaccineName,
            "itemId": selectedVaccine.vaccineId,
            "itemNumber": number,
            "itemNofChild": child,
            "currentStock": (number * 100) % 7
        });
        document.getElementById("vaccineSelectionDropdown").value=""
        resetName();
        resetNumber();
        resetChild();
    }
    var config = {
        method: 'get',
        url: 'http://localhost:8765/api/vaccines/',
        headers: {
            'auth': localStorage.getItem('vmisJwt')
        }
    };
    useEffect(() => {
        axios(config)
            .then(function (response) {
                setVaccineList(response.data)
            })
            .catch(function (error) {
                console.log(error);
            });
        return vaccineList
    }, [config.url])
    const handleApiSubmition=()=>{

    }
    return (
        <div>
            <h4 className='card-header text-center'>Request Vaccine</h4>
            <div className='container-fluid'>
                {value ?
                    <div className="card-body">
                        <div className='text-center'>
                            <div className='row'>
                                <div className='col-md-4'>
                                    <h2>Republic of Rwanda</h2>
                                    <h2>Ministry of Health</h2>
                                </div>
                                <div className='col-md-4'>
                                    <img src={Image} alt='myIg' /><br />
                                    <b>PEV/EPI</b>
                                </div>
                                <div className='col-md-4'>
                                    <b><h3>Date: {date}</h3></b>
                                </div>
                            </div>
                            <div className='alert alert-primary'>
                                <h3><b><u>VACCINE ORDER FORM</u></b></h3>
                            </div>
                            <div>
                                <table className="table table-bordered">
                                    <thead>
                                        <tr>
                                            <th scope="col"></th>
                                            <th scope="col">Children vaccinated last month</th>
                                            <th scope="col">No. of doses in stock</th>
                                            <th scope="col">Quantity ordered in doses</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {vaccineRequest.map(post => {
                                            return (
                                                <tr>
                                                    <th scope="row">{post.itemName}</th>
                                                    <td>{post.itemNofChild}</td>
                                                    <td>{post.currentStock}</td>
                                                    <td>{post.itemNumber}</td>
                                                </tr>
                                            )
                                        })}

                                    </tbody>
                                </table>
                                    <button className='btn btn-success' onClick={handleApiSubmition}>Submit</button>
                            </div>
                        </div>
                    </div> :
                    <div>
                        <div className='card-body row offset-md-1'>
                            <form className='col-md-5' onSubmit={handleSubmit}>
                                <div className="">
                                <div className='form-group'>
                                            <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                                            <select name="vaccineSelection" className='form-control' id="vaccineSelectionDropdown" required onChange={hanldeSelectBoxInput}>
                                                <option value="">None</option>
                                                {vaccineList ? vaccineList.map((x, index) => <option value={vaccineList[index].vaccineid}>{vaccineList[index].shortname}</option>) : ""}
                                            </select>
                                        </div>
                                    <div className='form-group'>
                                        <label>Children vaccinated last month:</label>
                                        <input type='text' name='child' className='form-control' placeholder='Number of children vaccinated last month' {...bindChild} />
                                    </div>
                                    <div className='form-group'>
                                        <label>Quantity:</label>
                                        <input type='text' name='quantity' className='form-control' placeholder='Quantity of needed item' {...bindNumber} />
                                    </div>
                                    <div className='form-group'></div>
                                    <div className='form-group'></div>
                                    <div className='form-group'>
                                        <input type="submit" value="Add on a list" className='btn btn-success form-control' />
                                    </div>
                                </div>
                            </form>
                            <div className='col-md-6 offset-md-1 text-center'>
                                {vaccineRequest.length > 0 ?
                                    <div>
                                        <br />
                                        <h3><u><b>List of items on request</b></u></h3>
                                        <br />
                                        <br />
                                        <div>
                                            <div>
                                                <div className='card'>
                                                    <table className="table table-striped table-bordered">
                                                        <thead>
                                                            <tr>
                                                                <th scope="col">Vaccine name</th>
                                                                <th scope="col">Current Stock</th>
                                                                <th scope="col">Requested</th>
                                                                <th scope="col">Last month usage</th>
                                                            </tr>
                                                        </thead>
                                                        <tbody>
                                                            {vaccineRequest.map(post => {
                                                                return (
                                                                    <tr>
                                                                        <th scope="row">{post.itemName}</th>
                                                                        <td>{post.currentStock}</td>
                                                                        <td>{post.itemNumber}</td>
                                                                        <td>{post.itemNofChild}</td>
                                                                    </tr>
                                                                )
                                                            })}
                                                        </tbody>
                                                    </table>
                                                </div>
                                            </div>
                                            <br />
                                            <button className='btn btn-success form-control' onClick={changeView}>Submit request  </button>
                                        </div>
                                    </div> : ""}
                            </div>
                        </div>
                    </div>}
            </div>
        </div >
    )
}

