import React from 'react';
import { SidebarData } from './SidebarData';
//import { Link } from 'react-router-dom';
import Image from '../MediaFiles/pev.png';
import styled from 'styled-components';
import { Link } from 'react-router-dom';


const Container = styled.div`
    margin-top: 1rem;
    margin-left: 0.3rem;
    margin-right: 0rem;
    padding: 0.5rem;
    border: 1px solid white;
    border-radius: 10px;
    position: relative;
    color: white;
`

const ProfileImg = styled.img`
    height: 3rem;
`
const ProfileName = styled.h2`
    font-size: 1rem;
    font-weight: 200;
    color: ${({ theme }) => theme.textColor};
`

function Sidebar() {
    return (
        <div className="">
                <Container>
                    <div className="text-center">
                        <ProfileImg className="text-center" src={Image} />
                    </div>
                    <h4 className='card-header text-center'><b>Profile</b></h4>
                    <ProfileName>
                        <div className="text-left">
                            <h4 className="text-center"><u><b>USER INFORMATION</b></u></h4>
                            <h5><b><u>Username:</u></b></h5>
                            <h4 className="text-center"><b>Test</b></h4>
                            <h5><b><u>Name:</u></b></h5>
                            <h4 className="text-center"><b>Test</b></h4>
                            <h5><b><u>Organization Unit:</u></b></h5>
                            <h4 className="text-center"><b>Test</b></h4>
                        </div>
                        <div className="text-center">
                            <button class="btn btn-info ">Change stock</button>
                        </div>
                    </ProfileName>
                </Container>

                <ul className="SidebarList">
                    {
                        SidebarData.map((val, key) => {
                            return (
                                <li
                                    key={key}
                                    className="row"
                                    id={window.location.pathname === val.path ? "active" : ""}
                                    onClick={() => { window.location.pathname = val.path; }}>
                                    <div id="icon">{val.icon}  </div>{" "}
                                    <div id="title">{val.title}  </div>
                                    <Link to=""></Link>
                                </li>
                            );
                        })
                    }
                </ul>

        </div>
    )
}

export default Sidebar
