import React, { useState } from 'react';
import { useInput } from '../Data/useInput';
import OrgData from './PopModalOrgUnits';
const distributeVaccine = []
const DistributionForm = () => {
    const [distributionData, setDistributionData] = useState('')
    const { value: vaccine, bind: bindVaccine, reset: resetVaccine } = useInput('');
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const [OrgUnitValue, setOrgUnitValue] = useState('')
    const handleSubmit = (evt) => {
        evt.preventDefault();
        distributeVaccine.push({
            "itemName": vaccine,
            "itemQuatity": quantity
        });

        resetVaccine();
        resetQuantity();
    }
    return (
        <div>
            <form onSubmit={handleSubmit} autoComplete="off">
                <OrgData btnName="Select Organisation Unit" btnColor="warning" onChange={(value) => setOrgUnitValue(value)} />
                <input type="hidden" name="OrgUnit" value={OrgUnitValue} />
                <div className='form-group'>
                    <h5><b>Send Vaccine</b></h5>
                    <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                    <select name="vaccineSelection" required id="mySelectFormElement" className='form-control' {...bindVaccine} required >
                        <option value="">None</option>
                        <option value="BCG">BCG</option>
                        <option value="DBCG">DBCG</option>
                        <option value="VPO">VPO</option>
                        <option value="IPV">IPV</option>
                        <option value="Pentavalent">Pentavalent</option>
                        <option value="Pneumocoque">Pneumocoque</option>
                        <option value="Rotavirus">Rotavirus</option>
                        <option value="MR">MR</option>
                        <option value="DMR">DMR</option>
                        <option value="VAT">VAT</option>
                    </select>
                </div>
                <div className='form-group'>
                    <label htmlFor='quantity'> Quantity:</label>
                    <input type='number' min='0' name='quantity' className='form-control' {...bindQuantity} required />
                </div>
                <div className='form-group'>
                    <button className='btn btn-success form-control'>Add on a list  </button>
                </div>
            </form>
        </div>
    );
}

export default DistributionForm;
