import React, { useState } from 'react';
import axios from 'axios'
import { useInput } from '../Data/useInput';
import { Tabs, Tab, Button, Spinner } from 'react-bootstrap/'
const returnList = []
const Redistribute = (props) => {
    const { value: vaccine, bind: bindVaccine, reset: resetVaccine } = useInput('');
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const [disabled, setDisabled] = useState(false)
    const [spin, setSpin] = useState('')
    const [error, setError] = useState('')
    const [Deleted,setDeleted]=useState(false)
    const handleSubmit = (e) => {
        e.preventDefault()
        returnList.push({
            "vaccineName": vaccine,
            "quantityReturned": quantity
        })
        console.log(returnList)
        resetVaccine()
        resetQuantity()
    }
    const deleteReturn = (e) => {
        returnList.splice(e,1)
        setInterval(() => {
            setDeleted(true)
        }, 50);
        setDeleted(false)
    }
    const handleApiSubmission = () => {
        setSpin('border');
        setDisabled(true);
        axios.get("https://dev.hisprwanda.org/vmis").then(res => {
            setDisabled(false)
            setError('')
            returnList.splice(0, returnList.length);
            setSpin('');
        }).catch(e => {
            setError('Redistribution failed please try again later');
            setSpin('');
            setDisabled(false);
        })
    }
    return (
        <div>
            <h4 className='card-header text-center'>Redistribution </h4>
            <div className="container-fluid my-3">
                <Tabs defaultActiveKey="Redistribution" transition={false} id="noanim-tab-example">
                    <Tab eventKey="Redistribution" title="Redistribution">
                        Redistribution
                            </Tab>
                    <Tab eventKey="redistributionForm" title="Return Form">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-6">
                                    <h3>Return Form</h3>
                                    <form onSubmit={handleSubmit}>
                                        <div className='form-group'>
                                            <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                                            <select name="vaccineSelection" className='form-control' {...bindVaccine} required disabled={disabled}>
                                                <option value="">None</option>
                                                <option value="BCG">BCG</option>
                                                <option value="DBCG">DBCG</option>
                                                <option value="VPO">VPO</option>
                                                <option value="IPV">IPV</option>
                                                <option value="Pentavalent">Pentavalent</option>
                                                <option value="Pneumocoque">Pneumocoque</option>
                                                <option value="Rotavirus">Rotavirus</option>
                                                <option value="MR">MR</option>
                                                <option value="DMR">DMR</option>
                                                <option value="VAT">VAT</option>
                                            </select>
                                        </div>
                                        <div className="form-group">
                                        </div>
                                        <div className="form-group">
                                            <input type="number" className="form-control" placeholder="Quantity to be returned" {...bindQuantity} required disabled={disabled} />
                                        </div>
                                        <button className="btn btn-primary" disabled={disabled}>Add to list</button>
                                    </form>
                                </div>{returnList.length > 0 ?
                                    <div className="col-6">
                                        <h3 className="my-3">Return Summary</h3>
                                        <table className="table table-striped table-bordered">
                                            <thead>
                                                <tr>
                                                    <th scope="col">Vaccine Name</th>
                                                    <th scope="col">Vaccine  Quantity </th>
                                                    <th scope="col">Action </th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {returnList.map((key, index) => (

                                                    <tr key={index}><td>{key.vaccineName}</td><td>{key.quantityReturned}</td><td><button  onClick={()=>deleteReturn(index)} className="btn btn-danger" disabled={disabled}>Delete</button></td></tr>
                                                ))}
                                            </tbody>
                                        </table>
                                        <p className="text-center text-danger">{error}</p>
                                        <Button variant="primary" disabled={disabled} onClick={handleApiSubmission}>
                                            <Spinner as="span" animation={spin} size="sm" role="status" aria-hidden="true" /> Submit</Button>

                                    </div> : ""}
                            </div>
                        </div>
                    </Tab>
                </Tabs>

            </div>
        </div>
    );
}

export default Redistribute;
