import axios from 'axios';
import React, { useState } from 'react';
import OrgData from './PopModalOrgUnits';
const RegistrationForm = () => {
    const [selectedOrgUnit, setselectedOrgUnit] = useState("")
    const [error, setError] = useState()
    const formData = { username: "", name: "", surname: "", email: "", phone: "", orgUnit: "", orgUnitLevel: 1, password: "" }
    function handleSubmit(event) {
        event.preventDefault();
        const form = event.currentTarget;
        formData.username = form[0].value; formData.name = form[1].value; formData.surname = form[2].value; formData.phone = form[3].value;
        formData.email = form[4].value; formData.password = form[5].value; formData.orgUnit = form[7].value;
        console.log(formData)
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }
        if (form.checkValidity() === true) {
            axios.post("http://localhost:5000/users", formData).then(res => {
            }).catch(e => {
                setError(e.config)
            })
        }
    }
    return (
        <div>
            <div className="container">
                <div className=''>

                    <div style={{ padding: "5px" }}>
                        <form onSubmit={handleSubmit} autoComplete="off">
                            <input type="text" className="formControl m-3" name="username" required placeholder="Username" />
                            <input type="text" className="formControl m-3" name="name" required placeholder="Name" />
                            <br />
                            <input type="text" className="formControl m-3" name="surname" required placeholder="Surname" />
                            <input type="number" className="formControl m-3" name="phone" required placeholder="Phone Number" />
                            <br />
                            <input type="email" className="formControl m-3" name="email" required placeholder="email" />
                            <input type="password" className="formControl m-3" name="password" required placeholder="Password" />
                            <br />
                            <div className="m-3">
                                <OrgData onChange={value => setselectedOrgUnit(value)} btnName="Select user Org Unit" btnColor="btn btn-warning" />
                                <input type="hidden" className="formControl" id="myOrgInput" value={selectedOrgUnit} name="orgUnit" />
                            </div>
                            <button className="btn btn-success m-3">Create user</button>
                            <span className="text-danger">{error}</span>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default RegistrationForm;
