import React from 'react'
import { useInput } from '../Data/useInput';
import RegistrationForm from '../Components/RegisterForm';
import { Tabs, Tab,Row,Col } from 'react-bootstrap'
import DataTables from '../Components/DataTables';
var newVaccineList = [];
var myDisplayList = [];
function Vaccines() {
    const { value: vaccineValue, bind: bindVaccineValue, reset: resetVaccineValue } = useInput('');
    const { value: expirationDateValue, bind: bindExpirationDateValue, reset: resetExpirationDateValue } = useInput('');
    const { value: batchNumberValue, bind: bindBatchNumberValue, reset: resetBatchNumberValue } = useInput('');
    const { value: quantityValue, bind: bindQuantityValue, reset: resetQuantityValue } = useInput('');
    const { value: numberOfDoseValue, bind: bindNumberOfDoseValue, reset: resetNumberOfDoseValue } = useInput('');
    const { value: pcvValue, bind: bindPcvValue, reset: resetPcvValue } = useInput('');
    const addItemOnList = (evt) => {
        evt.preventDefault();
        newVaccineList.push({
            "trackedEntityType": "yzInaMXBYDb",
            "orgUnit": "Hjw70Lodtf2",
            "attributes": [
                {
                    //Type
                    "attribute": "QdxqWF42fNB",
                    "value": vaccineValue
                },
                {
                    //expirationDate
                    "attribute": "lVXabcp6jee",
                    "value": expirationDateValue
                },
                {
                    //batchNumber
                    "attribute": "b7OrHNx7qXf",
                    "value": batchNumberValue
                },
                {
                    //Quality
                    "attribute": "yFCnGdrKcZO",
                    "value": quantityValue
                },
                {
                    //Number of dose in flacon
                    "attribute": "BFRA6drf9dV",
                    "value": numberOfDoseValue
                },
                {
                    //pcv status
                    "attribute": "vbr2G0ugn0R",
                    "value": pcvValue
                },
                {
                    //QWlJZRzSPN0
                    "attribute": "QWlJZRzSPN0",
                    "value": vaccineValue + "_" + batchNumberValue
                }
            ],
            "enrollments": [
                {
                    "orgUnit": "Hjw70Lodtf2",
                    "program": "cFFkVcCw1jR",
                    "enrollmentDate": "2020-10-13",
                    "incidentDate": "2020-10-13"
                }
            ]
        });
        myDisplayList.push({
            "vaccineName": vaccineValue,
            "expirationDate": expirationDateValue,
            "quantity": quantityValue,
            "batch": batchNumberValue
        });
        resetBatchNumberValue();
        resetExpirationDateValue();
        resetNumberOfDoseValue();
        resetPcvValue();
        resetQuantityValue();
        resetVaccineValue();

        console.log(newVaccineList);
    }
    return (
        <div>
            <h4 className='card-header text-center'>Vaccine / Users Management</h4>
            <div className='container-fluid my-2'>
                <Tabs defaultActiveKey="home" transition={false} id="noanim-tab-example">
                    <Tab eventKey="home" title="Vaccines">
