                            <form onSubmit={addItemOnList}>
                                <div className='row'>
                                    <div className="col-md-4 offset-md-1">
                                        <div className='form-group'>
                                            <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                                            <select name="vaccineSelection" className='form-control' {...bindVaccineValue}>
                                                <option value="BCG">BCG</option>
                                                <option value="DBCG">DBCG</option>
                                                <option value="VPO">VPO</option>
                                                <option value="IPV">IPV</option>
                                                <option value="Pentavalent">Pentavalent</option>
                                                <option value="Pneumocoque">Pneumocoque</option>
                                                <option value="Rotavirus">Rotavirus</option>
                                                <option value="MR">MR</option>
                                                <option value="DMR">DMR</option>
                                                <option value="VAT">VAT</option>
                                            </select>
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='expirationDate'>Expiration Date:</label>
                                            <input type='Date' name='expirationDate' className='form-control' {...bindExpirationDateValue} />
                                        </div>
                                        <div className='fom-group'>
                                            <label htmlFor='batchNumber'>Batch number:</label>
                                            <input type='text' name='batchNumber' className='form-control' {...bindBatchNumberValue} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='quantity'>Quantity:</label>
                                            <input type='text' name='quantity' className='form-control' {...bindQuantityValue} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='numOfDose'>Number of dose per vial:</label>
                                            <select name="numOfDose" className='form-control' {...bindNumberOfDoseValue} >
                                                <option value="">Select one</option>
                                                <option value="2">2</option>
                                                <option value="4">4</option>
                                                <option value="10">10</option>
                                                <option value="20">20</option>
                                            </select>
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='pcvStatus'>VVM (PCV) Status:</label>
                                            <select name="pcvStatus" className='form-control' {...bindPcvValue}>
                                                <option value="">Select one</option>
                                                <option value="1">1</option>
                                                <option value="2">2</option>
                                                <option value="3">3</option>
                                                <option value="4">4</option>
                                            </select>
                                        </div>
                                        <div className='form-group'></div>
                                        <div className='form-group'></div>
                                        <div className='form-group'>
                                            <button className='btn btn-success form-control' type="submit" >Add new vaccine</button>
                                        </div>
                                    </div>
                                    <div className="col-md-5 text-center">
                                        {myDisplayList.length > 0 ?
                                            <div>
                                                <br />
                                                <h3><u><b>List of new on vaccines</b></u></h3>
                                                <br />
                                                <br />
                                                <div>

                                                    <div>
                                                        <div className='card'>
                                                            <table class="table table-striped table-bordered">
                                                                <thead>
                                                                    <tr>
                                                                        <th scope="col">Vaccine name</th>
                                                                        <th scope="col">Quantity value</th>
                                                                        <th scope="col">Expiration date</th>
                                                                        <th scope="col">Batch number</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody>
                                                                    {myDisplayList.map(post => {
                                                                        return (
                                                                            <tr>
                                                                                <th scope="row">{post.vaccineName}</th>
                                                                                <td>{post.quantity}</td>
                                                                                <td>{post.expirationDate}</td>
                                                                                <td>{post.batch}</td>
                                                                            </tr>)
                                                                    })}
                                                                </tbody>
                                                            </table>
                                                        </div>
                                                    </div>
                                                    <br />
                                                    <button className='btn btn-success' type="submit">Add vaccine Stock</button>
                                                </div>
                                            </div> : ""}
                                    </div>
                                </div>
                            </form>
                        </div>
                    </Tab>
                    <Tab eventKey="profile" title="users">
                        <Row className="my-2">
                            <Col md={5}><RegistrationForm /></Col>
                            <Col md={6}><DataTables/></Col>
                            </Row>
                        </Tab>
                </Tabs>
            </div >
        </div>
    )
}
export default Vaccines
