import React, { useState } from 'react';
import styled from 'styled-components';
import { useInput } from '../Data/useInput';
import ConfirmDelete from '../Components/Delete'
import OrgData from '../Components/PopModalOrgUnits';
const Container = styled.div`
>.distribute{
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    }
`
const distributeVaccine=[]
function Distribute() {
    const [distributionData, setDistributionData] = useState('')
    const { value: vaccine, bind: bindVaccine, reset: resetVaccine } = useInput('');
    const { value: quantity, bind: bindQuantity, reset: resetQuantity } = useInput('');
    const vaccineHandover=(vac)=>{
        const mySelectElement=document.getElementById("mySelectFormElement");
        mySelectElement.value=vac
        mySelectElement.dispatchEvent(
            new MouseEvent('change', {
                bubbles: true,
                cancelable: true,
                view: window,
        }));
    }
    const handleSubmit = (evt) => {
        evt.preventDefault();
        distributeVaccine.push({
            "itemName": vaccine,
            "itemQuatity": quantity});

            resetVaccine();
            resetQuantity();
    }
    const pendingRequest = [ 
        {
            "requestTitle": "Vaccine request",
            "requestOrigin": " From Masaka Hospital",
            "request": {
                "BCG": "30",
                "DMR": "490",
                "IPV": "70",
                "VAT": "900",
                "DBCG": "805"
            },
            "responce": {
                "BCG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Item request",
            "requestOrigin": "From Kibagabaga Hospital",
            "request": {
                "BCG": "100",
                "DMR": "40",
                "IPV": "810",
                "VAT": "600",
                "DBCG": "50"
            },
            "responce": {
                "BCG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Item Request",
            "requestOrigin": "From Masaka Hospital",
            "request": {
                "BCG": "50",
                "DMR": "190",
                "IPV": "74",
                "VAT": "50",
                "DBCG": "800"
            },
            "responce": {
                "BCG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Vaccine Request",
            "requestOrigin": "From CHU/K",
            "request": {
                "BCG": "300",
                "DMR": "250",
                "IPV": "70",
                "VAT": "90",
                "DBCG": "500"
            },
            "responce": {
                "BCG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
        {
            "requestTitle": "Vaccine Request",
            "requestOrigin": "From Gahini Hospital",
            "request": {
                "BCG": "125",
                "DMR": "10",
                "IPV": "80",
                "VAT": "150",
                "DBCG": "1000"
            },
            "responce": {
                "BCG": "",
                "DMR": "",
                "IPV": "",
                "VAT": "",
                "DBCG": ""
            }
        },
    ]
    return (
        <div className='distribute'>
            <h2 className='card-header text-center'>Distribute Vaccine</h2>
            <br />
            <br />
            <div>
                <div className='row'>
                    <div className='col-md-3 text-center'>
                        <h3 className='card-header'><b>Pending request </b></h3>
                        <div>
                            <div>
                                <div>
                                    {pendingRequest.map(post => {
                                        return (
                                            <div>
                                                <hr />
                                                <div className="card w-100">
                                                    <div className="card-body">
                                                        <h5 className="card-title"><b>{post.requestOrigin}</b></h5>
                                                        {/* <PopRequest data={post}/> */}
                                                        <button className="btn btn-info" onClick={()=>{setDistributionData(post);distributeVaccine.splice(0,distributeVaccine.length)}}>Check this request</button>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>

                      {distributionData? <div className="col-md-9">
                           <div className="card">
                        <h3 className='card-header'><b>{distributionData.requestOrigin} </b></h3>
                        <div className="row">
                        <div className="col">
                           <table className="table table-striped table-bordered">
                                <thead>
                                    <tr>
                                        <th scope="col">Vaccine Name</th>
                                        <th scope="col">Quantity ordered</th>
                                        <th scope="col">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {distributionData? Object.entries(distributionData.request).map(e=>(
                                <tr key={e}><td key={e}>{e[0]}</td><td> {e[1]}</td><td><button onClick={()=>{vaccineHandover(e[0])}} className="btn btn-info">Distribute</button></td></tr>)):""}

                                </tbody>
                            </table>
                            </div>
                            <div className="col my-2">
                            <form onSubmit={handleSubmit} autoComplete="off">
                                <OrgData/>
                                <input type="hidden" name="OrgUnit" />
                            <div className='form-group'>
                                <h5><b>Respond Request</b></h5>
                            <label htmlFor='vaccineSelection'>Select Vaccine:</label>
                            <select name="vaccineSelection" required id="mySelectFormElement" className='form-control' {...bindVaccine} required >
                                <option value="">None</option>
                                <option value="BCG">BCG</option>
                                <option value="DBCG">DBCG</option>
                                <option value="VPO">VPO</option>
                                <option value="IPV">IPV</option>
                                <option value="Pentavalent">Pentavalent</option>
                                <option value="Pneumocoque">Pneumocoque</option>
                                <option value="Rotavirus">Rotavirus</option>
                                <option value="MR">MR</option>
                                <option value="DMR">DMR</option>
                                <option value="VAT">VAT</option>
                            </select>
                        </div>
                        <div className='form-group'>
                            <label htmlFor='quantity'>Quantity:</label>
                            <input type='number' min='0' name='quantity' className='form-control' {...bindQuantity} required/>
                        </div>
                        <div className='form-group'>
                            <button className='btn btn-success form-control'>Add on a list  </button>
                        </div>
                        </form>
                            </div>
                            </div>
                            <div className="col">

                    </div>
                    {distributeVaccine.length? <div><table className="table table-striped table-bordered">
                                <thead>
                                    <tr>
                                        <th scope="col">Vaccine Name</th>
                                        <th scope="col">Quantity Requested</th>
                                        <th scope="col">Quantity Distributed</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {distributeVaccine? distributeVaccine.map(key=>(
                                    <tr><td>{key.itemName}</td><td></td><td>{key.itemQuatity}</td><td><ConfirmDelete/></td></tr>
                                )):""}
                                </tbody>
                            </table>
                            <button className="btn btn-success btn-block">Distribute</button>
                            </div>:""}
                           </div>
                           </div> :""}            
                </div>
            </div>
        </div>
    )
}

export default Distribute
