import React, { useState } from 'react';
import { SidebarData } from './SidebarData';
import Image from '../MediaFiles/pev.png';
import OrgData from './PopModalOrgUnits';
import { NavLink, useLocation } from 'react-router-dom';
import * as AiIcons from 'react-icons/ai';
import axios from 'axios';

function Sidebar(props) {
    const user = {
        username: "digiraneza",
        auth: localStorage.getItem("vmisJwt")
    }
    const userData = JSON.stringify(user)
    const location = useLocation()
    const [selectedOrgUnit, setSelectedOrgUnit] = useState("")
    props.onChange(selectedOrgUnit)
    const handleLogout = () => {
        if (localStorage.getItem("vmisJwt") !== null) {
            axios.put('http://192.168.0.120:8765/api/auth', userData).then(res => {
                localStorage.removeItem("vmisJwt")
                window.location.reload()
            }).catch(error => {
                console.log(error)
            })
        }
    }
    return (
        <div className="">

            <div>

                <div className="m-2 p-2 border rounded text-light">
                    <div className="text-center">
                        <img className="text-center profile-img m-2" src={Image} />
                    </div>
                    <span className='card-header text-center'>Profile</span>

                    <div className="text-left">
                        <p>Username:</p>
                        <p><u>Name:</u></p>
                    </div>
                    <div className="text-center">
                        {location.pathname === "/" ?
                            <OrgData onChange={value => setSelectedOrgUnit(value)} btnName="Change Stock" btnColor="btn btn-info" /> : ""
                        }
                    </div>
                </div>
            </div>
            <ul className="SidebarList">
                {
                    SidebarData.map((val, key) => {
                        return (
                            <li key={key} className="row">
                                <NavLink className="row" to={val.path} key={key} exact activeClassName="active">
                                    <div className="row">
                                        <div id="icon">{val.icon}  </div>{" "}
                                        <div id="title">{val.title}  </div>
                                    </div>
                                </NavLink>

                            </li>
                        );
                    })
                }
                <li className="row" onClick={handleLogout}>
                    <div className="row">
                        <div id="icon"><AiIcons.AiOutlineLogout />  </div>{" "}
                        <div id="title">Logout </div>
                    </div>
                </li>
            </ul>

        </div>
    )
}

export default Sidebar
