import React, { useState } from 'react';
import './App.css';
import Sidebar from './Components/Sidebar';
import Stock from './Pages/Stock';
import AddVaccine from './Pages/AddVaccine';
import Distribute from './Pages/Distribute';
import Request from './Pages/Request';
import Wastage from './Pages/Wastage';
import NotFound from './Pages/NotFound';
import Acknowledge from './Pages/Acknowledge';
import Dispensed from './Pages/Dispensed';
import { AuthContext } from './Context/UseAuth';
import PrivateRoute from './Data/PrivateRoute';
import Login from './Pages/Login';
import { Redirect, useLocation } from 'react-router-dom';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import { createContext, useContext } from 'react';
//import Login from './Pages/Login'
//import { useEffect } from 'react';
//import axios from 'axios';
//import { Card, Col, Container, Form, Button, Row, Spinner } from 'react-bootstrap';

import { UseAuth } from "./Context/UseAuth";


