  return (

    <div className="App">

      <div className="h-100 row" style={{ margin: "0px", padding: "-20px" }}>
        {window.location.pathname === "/login" ? <div className="col-lg-2 Sidebar">
        </div> :
          <div className="col-lg-2 Sidebar">
            {{ authTokens, setAuthTokens: setTokens } ?
              <Sidebar onChange={value=>setStockIdId(value)} /> : ""
            }
          </div>
        }

        <div className="col-lg-10">
          <AuthContext.Provider value={{authTokens, setAuthTokens: setTokens }}>
            <Router>
              <Switch>
                <PrivateRoute path='/' exact component={Stock}>
                  <Stock/>
                  </PrivateRoute>
                <PrivateRoute path='/add' component={AddVaccine} />
                <PrivateRoute path='/distribute' component={Distribute} />
                <PrivateRoute path='/acknowledge' component={Acknowledge} />
                <PrivateRoute path='/dispensed' component={Dispensed} />
                <PrivateRoute path='/request' component={Request} />
                <PrivateRoute path='/wastage' component={Wastage} />
                <Route path='/login' component={Login} />
                <PrivateRoute path='*' component={NotFound} />
              </Switch>
            </Router>
          </AuthContext.Provider>
        </div>
      </div>
    </div>
  );
}

export default App;
