// `
// const ProfileName = styled.h2`
//     font-size: 1rem;
//     font-weight: 200;
//     color: ${({ theme }) => theme.textColor};
// `
function Sidebar(props) {
    const [selectedOrgUnit, setSelectedOrgUnit] = useState("")
    props.onChange(selectedOrgUnit)
    return (
        <div className="">
            <div>

                <div className="userInfo m-2 p-2">
                <div className="text-center">
                    <img className="text-center profile-img m-2" src={Image} />
                </div>
                <span className='card-header text-center'>Profile</span>

                    <div className="text-left">
                        <p>Username:</p>
                        <p><u>Name:</u></p>
                    </div>
                    <div className="text-center">
                        {window.location.pathname === "/" ?
                            <OrgData onChange={value => setSelectedOrgUnit(value)} btnName="Change Stock" btnColor="btn btn-info" /> : ""
                        }
                    </div>
                </div>
            </div>
            <ul className="SidebarList">
                {
                    SidebarData.map((val, key) => {
                        return (
                            <li key={key} className="row myClass">
                                <Link className="row" to={val.path} key={key} id={window.location.pathname === val.path ? "active" : ""}>
                                <div className="row">
                                <div id="icon">{val.icon}  </div>{" "}
                                <div id="title">{val.title}  </div>
                                </div>
                                </Link>

                            </li>
                        );
                    })
                }
            </ul>

        </div>
    )
}

export default Sidebar
