import React, { useContext, useState } from 'react'
import { useInput } from '../Data/useInput';
import { Tabs, Tab, Row, Col } from 'react-bootstrap'
import axios from 'axios';
import { userIntelContext } from '../App';
import VaccinesList from '../Components/VaccinesSelectDropDown';
var myDisplayList = [];
function Vaccines() {
    const userInfo = useContext(userIntelContext)
    const [success, setSuccess] = useState(false)
    const [message, setMessage] = useState('')
    const [selectedVaccine, setSelectedVaccine] = useState('')
    const [disabled, setDisabled] = useState(false)
    const { value: expirationDateValue, bind: bindExpirationDateValue, reset: resetExpirationDateValue } = useInput('');
    const { value: batchNumberValue, bind: bindBatchNumberValue, reset: resetBatchNumberValue } = useInput('');
    const { value: quantityValue, bind: bindQuantityValue, reset: resetQuantityValue } = useInput('');
    const { value: numberOfDoseValue, bind: bindNumberOfDoseValue, reset: resetNumberOfDoseValue } = useInput('');
    const { value: pcvValue, bind: bindPcvValue, reset: resetPcvValue } = useInput('');
    const { value: vaccineInputShortName, bind: bindVaccineInputShortName, reset: resetVaccineInputShortName } = useInput('');
    const { value: vaccineInputName, bind: bindvVaccineInputName, reset: resetvaccineInputName } = useInput('');
    const { value: vaccineDescription, bind: bindVaccineDescription, reset: resetVaccineDescription } = useInput('');
    const addItemOnList = (evt) => {
        evt.preventDefault();
        myDisplayList.push({
            "orgunit": userInfo.userToken.orgunit,
            "vaccineid": selectedVaccine.vaccineId,
            "vaccineName": selectedVaccine.vaccineName,
            "expirationDate": expirationDateValue,
            "quantity": quantityValue,
            "batch_number": batchNumberValue,
            "dose_per_vial": numberOfDoseValue,
            "vvm_status": pcvValue
        });
        document.getElementById("vaccineSelectionDropdown").value = ''
        resetBatchNumberValue();
        resetExpirationDateValue();
        resetNumberOfDoseValue();
        resetPcvValue();
        resetQuantityValue();
    }
    const handleApiSubmision = () => {
        setDisabled(true)
        const data = JSON.stringify(myDisplayList)
        axios.post('http://localhost:8765/api/vaccines', data, {
            headers: {
                "auth": localStorage.getItem("vmisJwt"),
                "Content-Type": "Application/Json"
            }
        }).then(res => {
            setMessage({ message: 'vacccine stock added success fully', type: 'text-center text-success' })
            setInterval(() => {
                setMessage('');
                myDisplayList.splice(0, myDisplayList.length);
                setDisabled(false)
            }, 3000);
        }).catch(er => {
            setSuccess(true)
            setMessage({ message: 'vacccine stock update failed', type: 'text-center text-danger' })
            setInterval(() => {
                setSuccess(false)
                setMessage('');
                setDisabled(false)
            }, 500);
        }).catch(postErr => {
            console.log(postErr)
        })
    }
    const handleVaccineInputSubmit = (e) => {
        e.preventDefault();
        const data = {
            "vaccineInputShortName": vaccineInputShortName.toUpperCase(),
            "vaccineInputName": vaccineInputName,
            "vaccineInputDescription": vaccineDescription,
        }
        axios.post('http://localhost:8765/api/vaccines/add', data, {
            headers: {
                "auth": localStorage.getItem("vmisJwt"),
                "Content-Type": "Application/Json"
            }
        }).then(res => {
            resetVaccineInputShortName();
            resetvaccineInputName();
            resetVaccineDescription()
            setMessage({ message: 'vacccine added success fully', type: 'alert alert-success m-2 text-center' })
            setInterval(() => {
                setMessage('');
            }, 3000);
        }).catch(er => {
            setMessage({ message: 'Something went wrong', type: 'alert alert-danger m-2 text-center'})
            setInterval(() => {
                setMessage('');
            }, 5000);
        })
    }
    return (
        <div>
            <h4 className='card-header text-center'>Vaccine / Users Management</h4>
            <p className={message.type}>{message.message}</p>
            <div className='container-fluid my-2'>
                <Tabs defaultActiveKey="home" transition={false} id="noanim-tab-example">
                    <Tab eventKey="home" title="Vaccines">
                        <div>
                            <form onSubmit={addItemOnList} autoComplete="off">
                                <div className='row'>
                                    <div className="col-md-4 offset-md-1">
                                        <VaccinesList onChange={value => setSelectedVaccine(value)} dependency={message} disabled={disabled} />
                                        <div className='form-group'>
                                            <label htmlFor='expirationDate'>Expiration Date:</label>
                                            <input type='Date' name='expirationDate' className='form-control' required disabled={disabled} {...bindExpirationDateValue} />
                                        </div>
                                        <div className='fom-group'>
                                            <label htmlFor='batchNumber'>Batch number:</label>
                                            <input type='number' name='batchNumber' className='form-control' disabled={disabled} required {...bindBatchNumberValue} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='quantity'>Quantity:</label>
                                            <input type='number' name='quantity' className='form-control' required disabled={disabled} {...bindQuantityValue} />
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='numOfDose'>Number of dose per vial:</label>
                                            <select name="numOfDose" className='form-control' disabled={disabled} required {...bindNumberOfDoseValue} >
                                                <option value="">Select one</option>
                                                <option value="2">2</option>
                                                <option value="4">4</option>
                                                <option value="10">10</option>
                                                <option value="20">20</option>
                                            </select>
                                        </div>
                                        <div className='form-group'>
                                            <label htmlFor='pcvStatus'>VVM (PCV) Status:</label>
                                            <select name="pcvStatus" className='form-control' disabled={disabled} required {...bindPcvValue}>
                                                <option value="">Select one</option>
                                                <option value="1">1</option>
                                                <option value="2">2</option>
                                                <option value="3">3</option>
                                                <option value="4">4</option>
                                            </select>
                                        </div>
                                        <div className='form-group'></div>
                                        <div className='form-group'>
                                            <button className='btn btn-success form-control' type="submit" disabled={disabled} >Add vaccine stock</button>
                                        </div>
                                    </div>
                                    <div className="col-md-5 text-center">
                                        {myDisplayList.length > 0 ?
                                            <div>
                                                <br />
                                                <h3><u><b>List of new on vaccines</b></u></h3>
                                                <br />
                                                <br />
                                                <div>
                                                    <div>
                                                        <div className='card'>
                                                            <table className="table table-striped table-bordered">
                                                                <thead>
                                                                    <tr>
                                                                        <th scope="col">Vaccine name</th>
                                                                        <th scope="col">Quantity value</th>
                                                                        <th scope="col">Expiration date</th>
                                                                        <th scope="col">Batch number</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody>
                                                                    {myDisplayList.map(post => {
                                                                        return (
                                                                            <tr key={post}>
                                                                                <th scope="row">{post.vaccineName}</th>
                                                                                <td>{post.quantity}</td>
                                                                                <td>{post.expirationDate}</td>
                                                                                <td>{post.batch_number}</td>
                                                                            </tr>)
                                                                    })}
                                                                </tbody>
                                                            </table>
                                                        </div>
                                                    </div>
                                                    <br />
                                                    <button className='btn btn-success' type="submit" onClick={handleApiSubmision} disabled={disabled}>Add vaccine Stock</button>
                                                </div>
                                            </div> : ""}
                                    </div>
                                </div>
                            </form>
                        </div>
                    </Tab>
                    {userInfo.userToken.orgunitlevel === '1' ?
                        <Tab eventKey="profile" title="Add new vaccines">
                            <Row className="my-2">
                                <Col>
                                    <form className="col-4" onSubmit={handleVaccineInputSubmit}>
                                        <div className="form-group">
                                            <label htmlFor="vaccineName">Vaccine short name</label>
                                            <input type="text" id="vaccineName" name="vaccineShortName" required {...bindVaccineInputShortName} className="form-control" placeholder="Short Name" required />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="vaccineName"> Name in full*</label>
                                            <input type="text" id="vaccineName" name="vaccineName" required {...bindvVaccineInputName} className="form-control" placeholder="Name" required />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="vaccineName">Description</label>
                                            <textarea id="vaccineDescription" name="vaccineDescription" required {...bindVaccineDescription} className="form-control" placeholder="Description" required>
                                            </textarea>
                                        </div>
                                        <button className="btn btn-primary">Add Vaccine</button>
                                    </form>
                                </Col>
                            </Row>
                        </Tab> : ""}
                </Tabs>
            </div >
        </div>
    )
}
export default Vaccines
