import { useEffect, useState } from 'react'
import axios from 'axios'
export function HttpRequest(config,dependency) {

    const [request, setRequest] = useState({
        data: null,
        loading: false,
        error: false,
        error_type: null,
    })

    useEffect(() => {
        setRequest({
            data: null,
            loading: true,
            error: false,
            error_type: null,
        })
        axios(config).then(response => {
            if (response.data !== null) {
                setRequest({
                    data: response.data,
                    loading: false,
                    error: false,
                    error_type: null,

                })
            }
        })
            .catch(e => {
                console.log(e)
                setRequest({
                    data: null,
                    loading: false,
                    error: true,
                    error_type: e,
                });
                if (e.response.status === 500) {
                    setRequest({
                        data: null,
                        loading: false,
                        error: true,
                        error_type: 500,
                    });
                }
                else if (e.response.status === 404) {
                    setRequest({
                        data: null,
                        loading: false,
                        error: true,
                        error_type: 404,
                    });
                }
            })
    }, [dependency])
    if (request !== null) {
        return request
    }
}
